let output = {};

const name =$('Get Notion page').first().json.name;

const sub = $('Get Notion page').first().json.property_subject;

output.destination = `https://nextcloud.yuvrajsingh.icu/remote.php/dav/files/yuvrajsingh/Documents/Notes/OneNote_Notes/${sub}/${name}.one`;

output.url = 'http://localhost:3069/open?file=C%3A%5CUsers%5CYuvrajSingh%5CNextcloud%5CDocuments%5CNotes%5COneNote_Notes%5C' + encodeURIComponent(`${sub}\\${name}.one`);

output.name = name;
output.subject = sub;

return { json: output };

// Robust comparison: only direct children, only .one files, strict basename match
const extractHrefValues = (text) => {
  const regex = /<d:href>(.*?)<\/d:href>/g;
  const out = [];
  let m;
  while ((m = regex.exec(text)) !== null) out.push(m[1]);
  return out;
};

// Normalize consistently
const norm = (s) => (s || '').trim().normalize('NFKC').toLowerCase();

// Inputs
const xml = $json.data || '';
const notionName = norm($('Mapping').first().json.name);

// Optional: pass the collection path you PROPFINDed (exact trailing slash form)
const basePath = norm($json.basePath || ''); // e.g., "/remote.php/dav/files/user/OneNote/Notebook/" lowercased

// Collect candidate file basenames from direct children only
const hrefs = extractHrefValues(xml);

// Keep only hrefs that are direct children of basePath (or if basePath not provided, fallback to .one at any depth)
const candidates = hrefs.filter((h) => {
  if (!h.endsWith('.one')) return false;
  try {
    const decoded = decodeURIComponent(h);
    if (basePath) {
      // Must start with basePath and have no additional slashes after basePath in the remaining segment
      if (!decoded.toLowerCase().startsWith($json.basePath.toLowerCase())) return false;
      const rest = decoded.slice($json.basePath.length);
      if (rest.includes('/')) return false; // exclude nested paths
    }
    return true;
  } catch {
    return false;
  }
});

// Map to normalized basenames (no extension)
const existingNames = candidates.map((h) => {
  const decoded = decodeURIComponent(h);
  const last = decoded.split('/').filter(Boolean).pop() || '';
  const base = last.replace(/\.[^/.]+$/, '');
  return norm(base);
});

// Strict equality against Notion name
const match = existingNames.includes(notionName);

return [{ json: { match } }]; // This means if condition of equal to met than true.
