// Extract search queries from research plan
const response = $input.item.json.choices[0].message.content;
let queries = [];

// Try to parse JSON first
try {
  queries = JSON.parse(response);
} catch (e) {
  // Fallback: extract queries from text
  const lines = response.split('\n');
  queries = lines
    .filter(line => line.trim().length > 10)
    .map(line => line.replace(/^[\d\-\.\*]\s*/, '').trim())
    .filter(q => q.length > 0)
    .slice(0, 5);
}

// Return array of queries for batch processing
return queries.map(query => ({
  json: {
    searchQuery: query,
    originalTopic: $input.item.json.query || $('Parse Form Input').item.json.query,
    sessionId: $input.item.json.sessionId || $('Parse Form Input').item.json.sessionId
  }
}));

// Combine all search results
const allResults = $input.all();
const formData = $('Parse Form Input').first().json;

let researchData = {
  topic: formData.query,
  context: formData.context,
  sources: []
};

allResults.forEach(item => {
  if (item.json.organic) {
    item.json.organic.forEach(result => {
      researchData.sources.push({
        title: result.title,
        snippet: result.snippet,
        link: result.link
      });
    });
  }
});

return [{
  json: {
    ...formData,
    research: researchData,
    sourceCount: researchData.sources.length
  }
}];

