// n8n Function node code
// Input: items where items[i].json.text contains the markdown-ish string
// Output: items with .json.html and .binary.data (base64 HTML) ready for HTML->PDF nodes

const sanitizeForHtml = (md) => {
  if (!md) return '';
  // Basic conversion: bold (**text**) -> <strong>text</strong>
  // This is intentionally simple ‚Äî for complex markdown use a markdown library.
  let html = md.replace(/\*\*(.+?)\*\*/gs, '<strong>$1</strong>');
  // Convert two or more newlines to paragraph separators
  html = html.replace(/\r\n/g, '\n');
  html = html.split('\n\n').map(p => p.trim()).filter(p => p !== '').map(p => {
    // inside a paragraph convert single newlines to <br>
    const withBr = p.replace(/\n/g, '<br>');
    return `<p>${withBr}</p>`;
  }).join('\n');
  return html;
};

const wrapFullHtml = (bodyHtml, title = 'Report') => {
  return `<!doctype html>
<html lang="en">
<head>
  <meta charset="utf-8"/>
  <meta name="viewport" content="width=device-width,initial-scale=1"/>
  <title>${title}</title>
  <style>
    body { font-family: Arial, Helvetica, sans-serif; font-size: 12pt; line-height:1.4; padding: 28px; color: #222; }
    h1 { font-size: 20pt; margin-bottom: 6px; }
    p { margin: 8px 0; }
    strong { font-weight: 700; }
    .footer { margin-top: 24px; font-size: 9pt; color: #666; }
    pre { white-space: pre-wrap; word-wrap: break-word; }
  </style>
</head>
<body>
  <h1>${title}</h1>
  ${bodyHtml}
  <div class="footer">Generated by n8n ‚Ä¢ ${new Date().toLocaleString()}</div>
</body>
</html>`;
};

// process all items
const output = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const rawText = item.json && (item.json.text || item.json.body || item.json.content) ? (item.json.text || item.json.body || item.json.content) : '';
  const title = (item.json && item.json.title) ? item.json.title : 'Report';
  const bodyHtml = sanitizeForHtml(rawText);
  const fullHtml = wrapFullHtml(bodyHtml, title);

  // place HTML in json for nodes that read html from json
  const newItem = {
    json: {
      ...item.json,
      html: fullHtml,
      _generatedTitle: title,
    },
    binary: {}
  };

  // attach binary (base64) ‚Äî many HTML->PDF nodes accept binary input
  const buffer = Buffer.from(fullHtml, 'utf8');
  newItem.binary.data = {
    fileName: `${title.replace(/[^a-z0-9]+/gi, '_').toLowerCase() || 'report'}.html`,
    mimeType: 'text/html',
    fileExtension: 'html',
    data: buffer.toString('base64'),
  };

  output.push(newItem);
}

return output;

