const rows = items
  .map(i => i.json)
  .filter(r => r && Object.keys(r).length > 0); 


if (rows.length === 0) {
  return [];
}

const summary = {};
for (const r of rows) {
  const metric = r.metric_name || 'unknown_metric';
  const severity = r.severity || 'unknown';
  const key = metric + '|' + severity;
  summary[key] = (summary[key] || 0) + 1;
}

const today = new Date();
const yesterday = new Date(today);
yesterday.setDate(today.getDate() - 1);
const dateStr = yesterday.toISOString().slice(0, 10);

let text = `Daily Product Health Report ‚Äì ${dateStr}\n\n`;
text += `Total incidents: ${rows.length}\n\n`;

for (const [key, count] of Object.entries(summary)) {
  const [metric, severity] = key.split('|');
  text += `‚Ä¢ ${metric} ‚Äì ${severity}: ${count}\n`;
}

text += `\n---\nThis email was sent automatically with n8n.`;

return [
  {
    json: {
      text,
      incidents: rows,
    },
  },
];

