const texts = [];

// Ambil data dari input pertama
const inputData = $input.first().json;

// Cek struktur data
if (inputData && inputData.results && inputData.results[0]) {
  const content = inputData.results[0].content;

  // Loop content
  for (const contentItem of content) {
    if (contentItem.transcriptSegmentRenderer) {
      const runs = contentItem.transcriptSegmentRenderer.snippet.runs;
      for (const run of runs) {
        texts.push(run.text);
      }
    }
  }
}

return {
  transcript: texts.join(' '),
  textArray: texts,
  totalSegments: texts.length
};

// Code Node: Parse YouTube Metadata
const inputData = $input.first().json;

// Helper function: Format duration (seconds to mm:ss or hh:mm:ss)
function formatDuration(seconds) {
  const hours = Math.floor(seconds / 3600);
  const minutes = Math.floor((seconds % 3600) / 60);
  const secs = seconds % 60;

  if (hours > 0) {
    return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  }
  return `${minutes}:${secs.toString().padStart(2, '0')}`;
}

// Helper function: Format number with commas
function formatNumber(num) {
  return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}

// Helper function: Format date (YYYYMMDD to readable format)
function formatDate(dateStr) {
  if (!dateStr) return null;
  const year = dateStr.substring(0, 4);
  const month = dateStr.substring(4, 6);
  const day = dateStr.substring(6, 8);
  return `${year}-${month}-${day}`;
}

// Extract metadata
let metadata = {};

if (inputData && inputData.results && inputData.results[0]) {
  const content = inputData.results[0].content.results;

  // Basic Info
  metadata.videoId = content.video_id || null;
  metadata.title = content.title || null;
  metadata.channel = content.uploader || null;
  metadata.channelId = content.channel_id || null;
  metadata.channelUrl = content.channel_url || null;

  // Duration & Date
  metadata.duration = content.duration || 0;
  metadata.durationFormatted = content.duration ? formatDuration(content.duration) : null;
  metadata.uploadDate = formatDate(content.upload_date);
  metadata.uploadDateRaw = content.upload_date || null;

  // Engagement Stats
  metadata.viewCount = content.view_count || 0;
  metadata.viewCountFormatted = content.view_count ? formatNumber(content.view_count) : "0";
  metadata.likeCount = content.like_count || 0;
  metadata.likeCountFormatted = content.like_count ? formatNumber(content.like_count) : "0";
  metadata.commentCount = content.comment_count || 0;
  metadata.commentCountFormatted = content.comment_count ? formatNumber(content.comment_count) : "0";

  // Content Context
  metadata.tags = content.tags || [];
  metadata.categories = content.categories || [];
  metadata.category = content.categories && content.categories.length > 0 ? content.categories[0] : null;

  // Chapters (filter out "Ad")
  metadata.allChapters = content.chapters || [];
  metadata.chapters = (content.chapters || [])
    .filter(chapter => chapter.title.toLowerCase() !== 'ad')
    .map(chapter => ({
      title: chapter.title,
      startTime: chapter.start_time,
      startTimeFormatted: formatDuration(chapter.start_time)
    }));

  metadata.chapterTitles = metadata.chapters.map(ch => ch.title);

  // Description
  metadata.description = content.description || null;
  metadata.descriptionShort = content.description 
    ? content.description.substring(0, 200) + '...' 
    : null;

  // Thumbnail (highest quality)
  metadata.thumbnails = content.thumbnails || [];
  metadata.thumbnail = content.thumbnails && content.thumbnails.length > 0
    ? content.thumbnails[content.thumbnails.length - 1].url
    : null;

  // Additional Info
  metadata.isLive = content.is_live || false;
  metadata.ageLimit = content.age_limit || 0;
  metadata.isTranscriptAvailable = content.is_transcript_available || false;

  // Video URL
  metadata.videoUrl = `https://www.youtube.com/watch?v=${content.video_id}`;
}

// Return parsed metadata
return {
  videoId: metadata.videoId,
  title: metadata.title,
  channel: metadata.channel,
  channelUrl: metadata.channelUrl,
  duration: metadata.durationFormatted,
  durationSeconds: metadata.duration,
  uploadDate: metadata.uploadDate,
  views: metadata.viewCountFormatted,
  viewsRaw: metadata.viewCount,
  likes: metadata.likeCountFormatted,
  likesRaw: metadata.likeCount,
  comments: metadata.commentCountFormatted,
  commentsRaw: metadata.commentCount,
  tags: metadata.tags,
  category: metadata.category,
  chapters: metadata.chapters,
  chapterTitles: metadata.chapterTitles,
  description: metadata.description,
  descriptionShort: metadata.descriptionShort,
  thumbnail: metadata.thumbnail,
  videoUrl: metadata.videoUrl,
  isLive: metadata.isLive,
  isTranscriptAvailable: metadata.isTranscriptAvailable,

  // Formatted summary for AI context
  summaryForAI: {
    basic: `"${metadata.title}" by ${metadata.channel}`,
    stats: `${metadata.viewCountFormatted} views ‚Ä¢ ${metadata.likeCountFormatted} likes ‚Ä¢ ${metadata.durationFormatted}`,
    content: `Category: ${metadata.category} | Tags: ${metadata.tags.slice(0, 5).join(', ')}`,
    structure: `Chapters: ${metadata.chapterTitles.join(' ‚Üí ')}`
  }
};

// Optimized error handler - clean & informative
const errorData = $input.first().json;

function getNestedValue(obj, path, defaultValue = 'Unknown') {
  return path.split('.').reduce((curr, prop) => 
    curr?.[prop], obj) || defaultValue;
}

const workflowName = getNestedValue(errorData, 'workflow.name', 'Unknown Workflow');
const nodeName = getNestedValue(errorData, 'execution.error.node.name', 'Unknown Node');
const errorMessage = getNestedValue(errorData, 'execution.error.message', 'No error message');
const timestamp = getNestedValue(errorData, 'execution.startedAt', new Date().toISOString());
const executionId = getNestedValue(errorData, 'execution.id', 'Unknown');

// Tangkap error details
const errorObj = errorData.execution?.error || {};
const errorDescription = errorObj.description || '';
const errorHttpCode = errorObj.httpCode || '';

// Stack trace - ambil HANYA baris pertama yang paling penting
const errorStack = errorObj.stack || '';
const mainError = errorStack.split('\n')[0] || '';

// Format detail yang clean
let errorDetails = [];
if (errorDescription && errorDescription !== errorMessage) {
  errorDetails.push(errorDescription);
}
if (errorHttpCode) {
  errorDetails.push(`HTTP ${errorHttpCode}`);
}

const detailText = errorDetails.length > 0 
  ? errorDetails.join(' | ') 
  : 'No additional details';

// Main error dari stack (tanpa "NodeApiError: " prefix)
const cleanMainError = mainError.replace(/^(NodeApiError|Error):\s*/, '').trim();

// Build message - COMPACT VERSION
const message = `üö® <b>WORKFLOW ERROR</b>\n\n` +
  `üìã <b>${workflowName}</b>\n` +
  `‚öôÔ∏è Node: <code>${nodeName}</code>\n\n` +
  `‚ùå <b>${errorMessage}</b>\n` +
  `üí° ${detailText}\n\n` +
  `üïê ${new Date(timestamp).toLocaleString('id-ID', { 
    day: '2-digit', 
    month: 'short', 
    hour: '2-digit', 
    minute: '2-digit' 
  })}\n` +
  `üîó Exec: <code>${executionId}</code>`;

return {
  json: {
    message: message
  }
};

// Code Node: Split Long TLDR into Telegram Messages (HTML Format)

const inputData = $input.first().json;
const fullText = inputData.text;
const metadata = inputData;

const maxCharsPerChunk = 4000;

// Helper: Escape HTML special characters
function escapeHtml(text) {
  return text
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;');
}

// Helper: Convert Markdown-style formatting to HTML
function convertMarkdownToHtml(text) {
  let html = escapeHtml(text);

  // Bold: **text** atau *text* (hanya yang standalone)
  html = html.replace(/\*\*(.+?)\*\*/g, '<b>$1</b>');
  html = html.replace(/\*([^\*\n]+?)\*/g, '<b>$1</b>');

  // Italic: _text_
  html = html.replace(/_([^_\n]+?)_/g, '<i>$1</i>');

  // Code: `code`
  html = html.replace(/`([^`]+?)`/g, '<code>$1</code>');

  // Links: [text](url)
  html = html.replace(/\[([^\]]+)\]\(([^\)]+)\)/g, '<a href="$2">$1</a>');

  // Headers (convert to bold)
  html = html.replace(/^#+\s+(.+)$/gm, '<b>$1</b>');

  // Bullet points: * item or - item
  html = html.replace(/^[\*\-]\s+(.+)$/gm, '  ‚Ä¢ $1');

  // Numbered lists: 1. item
  html = html.replace(/^\d+\.\s+(.+)$/gm, (match, p1, offset, string) => {
    // Ambil nomor dari beginning of match
    const num = match.match(/^(\d+)\./)[1];
    return `  ${num}. ${p1}`;
  });

  return html;
}

// Helper: Add extra spacing after bullet points
function formatTextWithBulletSpacing(text) {
  return text
    .replace(/(\*\s+[^\n]+)\n(?!\n)/g, '$1\n\n')
    .replace(/(\d+\.\s+[^\n]+)\n(?!\n)/g, '$1\n\n')
    .replace(/(-\s+[^\n]+)\n(?!\n)/g, '$1\n\n')
    .replace(/\n{3,}/g, '\n\n');
}

// Helper: Split text intelligently
function splitTextIntoChunks(text, maxLength) {
  const chunks = [];
  const formattedText = formatTextWithBulletSpacing(text).trim();
  const paragraphs = formattedText.split('\n\n');

  let currentChunk = '';

  for (const paragraph of paragraphs) {
    const trimmedParagraph = paragraph.trim();
    if (!trimmedParagraph) continue;

    // Convert to HTML sebelum test length
    const htmlParagraph = convertMarkdownToHtml(trimmedParagraph);

    const testChunk = currentChunk 
      ? currentChunk + '\n\n' + htmlParagraph 
      : htmlParagraph;

    if (testChunk.length > maxLength) {
      if (currentChunk) {
        chunks.push(currentChunk);
      }

      if (htmlParagraph.length > maxLength) {
        const lines = trimmedParagraph.split('\n');
        let tempChunk = '';

        for (const line of lines) {
          const trimmedLine = line.trim();
          if (!trimmedLine) continue;

          const htmlLine = convertMarkdownToHtml(trimmedLine);
          const testLine = tempChunk 
            ? tempChunk + '\n' + htmlLine 
            : htmlLine;

          if (testLine.length > maxLength) {
            if (tempChunk) chunks.push(tempChunk);
            tempChunk = htmlLine;
          } else {
            tempChunk = testLine;
          }
        }

        if (tempChunk) {
          currentChunk = tempChunk;
        }
      } else {
        currentChunk = htmlParagraph;
      }
    } else {
      currentChunk = testChunk;
    }
  }

  if (currentChunk) {
    chunks.push(currentChunk);
  }

  return chunks;
}

// Split text
const textChunks = splitTextIntoChunks(fullText, maxCharsPerChunk);

// Create output items
const outputItems = textChunks.map((chunk, index) => {
  const isFirst = index === 0;
  const isLast = index === textChunks.length - 1;

  let messageText = '';

  // Header dengan HTML formatting
  if (isFirst) {
    messageText = `üì∫ <b>TLDR Summary</b>\n\n` +
                  `<b>${escapeHtml(metadata.title)}</b>\n\n` +
                  `üë§ ${escapeHtml(metadata.channel)}\n` +
                  `‚è±Ô∏è ${metadata.duration} | üëÅÔ∏è ${metadata.views} views\n\n` +
                  `üìÑ Part ${index + 1}/${textChunks.length}\n` +
                  `${'‚îÄ'.repeat(40)}\n\n`;
  } else {
    messageText = `üìÑ Part ${index + 1}/${textChunks.length}\n` +
                  `${'‚îÄ'.repeat(40)}\n\n`;
  }

  // Add content (sudah dalam format HTML)
  messageText += chunk;

  // Footer
  if (isLast) {
    messageText += `\n\n${'‚îÄ'.repeat(40)}\n\n` +
                   `‚úÖ End of Summary\n\n` +
                   `üîó <a href="${metadata.videoUrl}">Watch Video</a>`;
  }

  return {
    json: {
      messageNumber: index + 1,
      totalMessages: textChunks.length,
      text: messageText,
      charCount: messageText.length,
      chatId: $('On New Message').item.json.message.chat.id,
      parseMode: 'HTML', // Tambahkan ini

      videoId: metadata.videoId,
      title: metadata.title,
      videoUrl: metadata.videoUrl
    }
  };
});

return outputItems;

