// Retrieve input data
const item = $input.item.json;

// Magic function: Deep search for "AI Response"
function findAiJsonString(obj) {
  if (!obj) return null;
  if (typeof obj === 'string') {
    if (obj.includes('is_nsfw') || obj.includes('```json')) {
      return obj;
    }
    return null;
  }
  if (typeof obj === 'object') {
    for (const key in obj) {
      if (Object.prototype.hasOwnProperty.call(obj, key)) {
        const result = findAiJsonString(obj[key]);
        if (result) return result;
      }
    }
  }
  return null;
}

let aiText = findAiJsonString(item);

if (!aiText) {
  return {
    error: "Could not find AI response text.",
    debug_input: item 
  };
}

const cleanJson = aiText
  .replace(/```json/g, '')
  .replace(/```/g, '')
  .trim();

try {
  return JSON.parse(cleanJson);
} catch (e) {
  return { 
    error: "Failed to parse JSON.", 
    raw_text: aiText 
  };
}

// Function to automatically search for tag info
function findTagsInNode(nodeName) {
  try {
    const nodeData = $(nodeName).last();
    if (!nodeData || !nodeData.json) return null;

    function searchRecursive(obj) {
      if (!obj) return null;
      if (typeof obj === 'string') {
        if (obj.includes(',')) return obj;
        return null;
      }
      if (Array.isArray(obj)) {
        for (const item of obj) {
          const found = searchRecursive(item);
          if (found) return found;
        }
      }
      if (typeof obj === 'object') {
        if (obj.text) {
          const found = searchRecursive(obj.text);
          if (found) return found;
        }
        for (const key in obj) {
          const found = searchRecursive(obj[key]);
          if (found) return found;
        }
      }
      return null;
    }
    return searchRecursive(nodeData.json);
  } catch (e) {
    return null;
  }
}

const portraitTags = findTagsInNode('Generate Portrait Tags');
const landscapeTags = findTagsInNode('Generate Landscape Tags');
const finalTags = portraitTags || landscapeTags || $('Parse AI Response').item.json.description;

return {
  ...$input.item.json,
  final_tags: finalTags,
  final_date: new Date().toISOString()
};

