const items = $input.all();
const validItems = [];

items.forEach(item => {
  let url = item.json.link || item.json.url || item.json.guid || item.json.originallink;

  if (Array.isArray(url)) {
    url = url[0];
  }

  if (url && typeof url === 'string') {
    url = url.trim();

    if (url.length > 0) {
      validItems.push({
        json: {
          url: url,
          title: (item.json.title || 'No Title').trim(),
          source: item.json.source || 'unknown',
          pubDate: item.json.pubDate || item.json.isoDate
        }
      });
    }
  }
});

return validItems;
