parameters.jsonBody:
={
  "url_to_scrape": "https://www.amazon.com/s?k={{ $('On form submission').item.json['search query'] }}&page={{ $json.counter }}",
  "formats": [
    "json"
  ],
  "wait_before_scraping": 6000,
  "remove_css_selectors": "default",
  "llm_extract": {
    "schema": {
      "type": "array",
      "description": "A list of products listed on Amazon.",
      "items": {
        "type": "object",
        "properties": {
          "title": {
            "type": "string",
            "description": "The title of the product."
          },
          "url": {
            "type": "string",
            "description": "The full url for the product."
          }
        },
        "required": [
          "title",
          "url"
        ]
      }
    }
  },
  "screen_size": {
    "screen_type": "desktop",
    "screen_width": 1920,
    "screen_height": 1080
  }
}
