parameters.jsCode:
// Input: Array of Salesforce Line Items
const items = $input.all();

let html = `
<h2>Timesheet Line Items Summary</h2>
<table border="1" cellspacing="0" cellpadding="5" style="border-collapse: collapse; width: 100%;">
<tr>
  <th>Activity</th>
  <th>Type</th>
  <th>Billable</th>
  <th>Billable Amount</th>
  <th>Date</th>
  <th>Duration (hrs)</th>
  <th>Description</th>
</tr>
`;

for (const item of items) {
  const row = item.json;
  html += `
  <tr>
    <td>${row.dbt__Activity__c || ''}</td>
    <td>${row.dbt__Type__c || ''}</td>
    <td>${row.dbt__Billable__c || ''}</td>
    <td>${row.dbt__Billable_Amount__c || ''}</td>
    <td>${row.dbt__Date__c || ''}</td>
    <td>${row.dbt__Duration__c || ''}</td>
    <td>${row.dbt__Description__c || ''}</td>
  </tr>
  `;
}

html += `</table>`;

return [
  {
    json: {
      employeeId:$input.first().json.dbt__Employee__c, // <-- employee Id
      html
    }
  }
];

let body = "Hi, \n\nPlease find the weekly timesheet summary for all employees:\n\n";

items.forEach(item => {
  const t = item.json;

  body += `üë§ Employee: ${t.Name}\n`;
  body += `üìÖ Period: ${t.dbt__Start_Date__c} ‚Üí ${t.dbt__End_Date__c}\n`;
  body += `üìå Status: ${t.dbt__Status__c}\n`;
  body += `üïí Total Hours: ${t.dbt__Total_Hours__c} hrs\n`;
  body += ` - Billable: ${t.dbt__Billable_Hours__c} hrs\n`;
  body += ` - Non-Billable: ${t.dbt__Non_Billable_Hours__c} hrs\n`;
  body += ` - Absence: ${t.dbt__Absence_Hours__c} hrs\n`;
  body += `Weekly Requirement Met: ${t.dbt__Met_Weekly_Hours__c ? "‚úîÔ∏è Yes" : "‚ùå No"}\n\n`;

  const summary = t.message?.content?.summary || [];

  if (summary.length > 0) {
    body += "üìÇ Timesheet Line Items Breakdown:\n";
    summary.forEach(line => {
      body += ` - ${line}\n`;
    });
  } else {
    body += "üìÇ Timesheet Line Items Breakdown: No project details available\n";
  }

  body += `\n-------------------------------------------\n\n`;
});

return [{
  json: {
    emailSubject: "üìä Weekly Timesheet Summary ‚Äì All Employees",
    emailBody: body,
    managerEmail: [
      "user@example.com",
      "user@example.com",
      "user@example.com"
    ].join(",") // üëà convert array to string
  }
}];

// Get employees from input
const items = $input.all();

// Get week period from the first item
const first = items[0].json;
const startDate = first.dbt__Start_Date__c;
const endDate = first.dbt__End_Date__c;

let body =`The following employees did not submit their timesheet for the previous week :\n`;
body += `üóìÔ∏è ${startDate} ‚Üí ${endDate}\n\n`;

items.forEach((employee, index) => {
  const e = employee.json;

  body += `${index + 1}. ${e.Name} \n`;
  body += `üìß Email: ${e.dbt__Email__c}\n\n`;
});


return [{
  json: {
    emailSubject: "‚ö†Ô∏è Timesheet Not Submitted ‚Äì Previous Week",
    emailBody: body,
    managerEmail: [
      "user@example.com",
      "user@example.com",
      "user@example.com"
    ].join(",") // üëà convert array to string
  }
}];

// items[0] => Code1 output (All employees summary)
// items[1] => Code2 output (Not submitted list)

const allEmployees = items.find(i => i.json.emailSubject.includes("Weekly Timesheet Summary"));
const notSubmitted = items.find(i => i.json.emailSubject.includes("Not Submitted"));

let body = "";

// Add first email
if (allEmployees) {
  body += allEmployees.json.emailBody.trim();
  body += "\n\n";
}

// Separator
body += "YOUR_AWS_SECRET_KEY_HERE===\n";
body += "üü• TIMESHEET NOT SUBMITTED üü•\n\n";

// Add second email
if (notSubmitted) {
  body += notSubmitted.json.emailBody.trim();
}

// Prepare final single email
return [{
  json: {
    emailSubject: "üìä Weekly Timesheet Report + Pending Submissions ‚ö†Ô∏è ",
    emailBody: body,
    managerEmail: [
      "user@example.com",
      "user@example.com",
      "user@example.com"
    ].join(",") // üëà convert array to string
  }
}];
