// Parse AI output and return [{url, source}]
let results = [];

for (const item of items) {
  let data = item.json.output ?? item.json;

  // Clean markdown fences if present
  if (typeof data === "string") {
    let cleaned = data
      .replace(/```json\s*/gi, "")
      .replace(/```\s*/g, "")
      .trim();

    try {
      data = JSON.parse(cleaned);
    } catch (e) {
      // If it's just a raw URL string, wrap it
      if (/^https?:\/\//i.test(cleaned)) {
        results.push({ url: cleaned, source: "unknown" });
      }
      continue;
    }
  }

  // If we got an array of objects
  if (Array.isArray(data)) {
    data.forEach(entry => {
      if (entry && typeof entry === "object" && entry.url) {
        const normalized = normalizeUrl(entry.url);
        if (normalized) {
          results.push({
            url: normalized,
            source: entry.source ?? "unknown"
          });
        }
      }
    });
  }

  // If it's a single object
  if (data && typeof data === "object" && !Array.isArray(data) && data.url) {
    const normalized = normalizeUrl(data.url);
    if (normalized) {
      results.push({
        url: normalized,
        source: data.source ?? "unknown"
      });
    }
  }
}

// Helper: normalize URL - strip query params, hash, trailing slash
function normalizeUrl(url) {
  try {
    let u = new URL(url);

    // Remove tracking parameters
    const trackingParams = ['utm_source', 'utm_medium', 'utm_campaign', 'utm_content', 'utm_term', 'ref', 'source', 'fbclid', 'gclid'];
    trackingParams.forEach(param => u.searchParams.delete(param));

    // If no other params remain, clear search entirely
    if (u.searchParams.toString() === '') {
      u.search = '';
    }

    u.hash = "";
    return u.href.replace(/\/$/, "");
  } catch (e) {
    return null;
  }
}

// Deduplicate by URL
const seen = new Set();
results = results.filter(r => {
  if (seen.has(r.url)) return false;
  seen.add(r.url);
  return true;
});

return results.map(r => ({ json: r }));
