const workflow = $('Find Workflow').item.json || $('Create Workflow').item.json;
const error = $('Insert Error').item.json;
const aiAnalysis = $('AI Agent').item.json.output;

return [{
  json: {    
    workflow_name: workflow.workflow_name,
    failed_node: error.failed_node,
    error_message: error.error_message.substring(0, 300),
    execution_url: error.execution_url,
    ai_analysis: aiAnalysis,
    error_id: error.error_id
  }
}];

const errorTrigger = $('Workflow Error').item.json;
const workflowData = $('Get Workflow').item.json;
const executionData = $('Get Execution').item.json;

return [{
  json: {
    n8n_workflow_id: errorTrigger.workflow.id,
    workflow_name: errorTrigger.workflow.name,
    error_message: errorTrigger.execution.error.message || 'Unknown error',
    error_stack: errorTrigger.execution.error.stack || '',
    failed_node: errorTrigger.execution.lastNodeExecuted || 'Unknown',
    execution_id: errorTrigger.execution.id || 'manual',
    execution_url: errorTrigger.execution.url || '',
    workflow_json: JSON.stringify(workflowData),
    execution_data: JSON.stringify(executionData)
  }
}];

