// Universal fallback extractor for unknown sources
const results = [];

for (const item of items) {
  const html = item.json.data || item.json.body || "";
  const requestUrl = item.json.URL || item.json.url || "";
  const source = item.json.Source || "unknown";

  let title = null;
  let description = null;
  let author = null;
  let datePublished = null;
  let imageUrl = null;
  let canonicalUrl = null;

  // --- TITLE ---
  const titlePatterns = [
    /<h1[^>]*>([\s\S]*?)<\/h1>/i,
    /<meta[^>]+property=["']og:title["'][^>]+content=["']([^"']+)["']/i,
    /<title[^>]*>([\s\S]*?)<\/title>/i
  ];
  for (const pattern of titlePatterns) {
    const match = html.match(pattern);
    if (match) {
      title = match[1].replace(/<[^>]+>/g, "").trim();
      if (title) break;
    }
  }

  // --- DESCRIPTION ---
  const descPatterns = [
    /<meta[^>]+name=["']description["'][^>]+content=["']([^"']+)["']/i,
    /<meta[^>]+property=["']og:description["'][^>]+content=["']([^"']+)["']/i
  ];
  for (const pattern of descPatterns) {
    const match = html.match(pattern);
    if (match) {
      description = match[1].trim();
      if (description) break;
    }
  }

  if (!description) {
    const paragraphs = [...html.matchAll(/<p[^>]*>([\s\S]*?)<\/p>/gi)]
      .map(m => m[1].replace(/<[^>]+>/g, "").trim())
      .filter(t => t && t.length > 50);
    if (paragraphs.length) {
      description = paragraphs.slice(0, 2).join(" ").substring(0, 500);
    }
  }

  // --- AUTHOR ---
  const authorPatterns = [
    /<meta[^>]+name=["']author["'][^>]+content=["']([^"']+)["']/i,
    /by\s+([A-Z][a-z]+\s+[A-Z][a-z]+)/i,
    /<a[^>]+rel=["']author["'][^>]*>([^<]+)<\/a>/i
  ];
  for (const pattern of authorPatterns) {
    const match = html.match(pattern);
    if (match) {
      author = match[1].trim();
      if (author) break;
    }
  }

  if (!author) {
    const ldMatch = html.match(/<script[^>]+application\/ld\+json[^>]*>([\s\S]*?)<\/script>/i);
    if (ldMatch) {
      try {
        const ld = JSON.parse(ldMatch[1]);
        const a = ld.author;
        if (a) {
          author = typeof a === "string" ? a : (a.name || (Array.isArray(a) ? a[0].name : null));
        }
      } catch (e) {}
    }
  }

  // --- DATE PUBLISHED ---
  const datePatterns = [
    /<time[^>]+datetime=["']([^"']+)["']/i,
    /<meta[^>]+property=["']article:published_time["'][^>]+content=["']([^"']+)["']/i,
    /(\d{4}-\d{2}-\d{2})/
  ];
  for (const pattern of datePatterns) {
    const match = html.match(pattern);
    if (match) {
      datePublished = match[1].trim();
      if (datePublished) break;
    }
  }

  if (!datePublished) {
    const ldMatch = html.match(/<script[^>]+application\/ld\+json[^>]*>([\s\S]*?)<\/script>/i);
    if (ldMatch) {
      try {
        const ld = JSON.parse(ldMatch[1]);
        datePublished = ld.datePublished || ld.dateCreated || null;
      } catch (e) {}
    }
  }

  // --- IMAGE URL ---
  const imgPatterns = [
    /<meta[^>]+property=["']og:image["'][^>]+content=["']([^"']+)["']/i,
    /<meta[^>]+name=["']twitter:image["'][^>]+content=["']([^"']+)["']/i
  ];
  for (const pattern of imgPatterns) {
    const match = html.match(pattern);
    if (match) {
      imageUrl = match[1].trim();
      if (imageUrl) break;
    }
  }

  // --- CANONICAL URL ---
  const canonicalMatch = html.match(/<link[^>]+rel=["']canonical["'][^>]+href=["']([^"']+)["']/i);
  if (canonicalMatch) {
    canonicalUrl = canonicalMatch[1].trim();
  } else {
    const ogUrlMatch = html.match(/<meta[^>]+property=["']og:url["'][^>]+content=["']([^"']+)["']/i);
    if (ogUrlMatch) canonicalUrl = ogUrlMatch[1].trim();
  }

  results.push({
    json: {
      title,
      description,
      author,
      datePublished,
      imageUrl,
      canonicalUrl: canonicalUrl || requestUrl,
      source,
      sourceUrl: requestUrl
    }
  });
}

return results;

// Normalize extracted data from site-specific extractors
const results = [];

for (const item of items) {
  const input = item.json;
  const loopData = $('Loop Over URLs').item.json;

  results.push({
    json: {
      title: (input.title && input.title.trim()) ? input.title.trim() : null,
      description: (input.description && input.description.trim()) ? input.description.trim().substring(0, 1000) : null,
      author: (input.author && input.author.trim()) ? input.author.trim() : null,
      datePublished: (input.datePublished && input.datePublished.trim()) ? input.datePublished.trim() : null,
      imageUrl: (input.imageUrl && input.imageUrl.trim()) ? input.imageUrl.trim() : null,
      canonicalUrl: (input.canonicalUrl && input.canonicalUrl.trim()) ? input.canonicalUrl.trim() : loopData.URL,
      source: loopData.Source || "unknown",
      sourceUrl: loopData.URL
    }
  });
}

return results;

