// Calculate tier based on article age
const results = [];

for (const item of items) {
  const input = item.json;
  let tier = "Unknown";
  let freshnessStatus = "Fresh";

  if (input.datePublished) {
    const dateStr = input.datePublished;
    let articleDate;

    if (dateStr.match(/^\d{4}-\d{2}-\d{2}/)) {
      articleDate = new Date(dateStr);
    } else {
      const cleaned = dateStr.replace(/(\d+)(st|nd|rd|th)/, '$1');
      articleDate = new Date(cleaned);
    }

    if (!isNaN(articleDate.getTime())) {
      const now = new Date();
      const daysDiff = Math.floor((now - articleDate) / (1000 * 60 * 60 * 24));

      if (daysDiff <= 7) {
        tier = "Tier 1";
        freshnessStatus = "Fresh";
      } else if (daysDiff <= 14) {
        tier = "Tier 2";
        freshnessStatus = "Fresh";
      } else if (daysDiff <= 30) {
        tier = "Tier 3";
        freshnessStatus = "Fresh";
      } else {
        tier = "Archive";
        freshnessStatus = "Fresh";
      }
    }
  }

  results.push({
    json: {
      ...input,
      tier,
      freshnessStatus,
      extractedAt: new Date().toISOString()
    }
  });
}

return results;
