// Ottieni l'input dal nodo precedente
const inputData = $input.all();

// Array per raccogliere tutti i tag IDs
let allTagIds = [];

// Itera attraverso tutti gli item in input
for (const item of inputData) {
  const tags = item.json.tags;

  // Se tags esiste ed √® un array
  if (Array.isArray(tags)) {
    // Itera attraverso ogni array dentro tags
    for (const tagArray of tags) {
      // Se √® un array, aggiungi tutti gli ID
      if (Array.isArray(tagArray)) {
        allTagIds = allTagIds.concat(tagArray);
      }
    }
  }
}

// Rimuovi eventuali duplicati (opzionale)
allTagIds = [...new Set(allTagIds)];

// Restituisci il risultato come oggetto con l'array dentro
return [{ json: { tags: allTagIds } }];
