// Extract filename from URL and check for valid PDF
const results = [];

for (const item of items) {
  const pdfUrl = item.json.PDF_URL || item.json.pdf_url || item.json.url || "";

  if (!pdfUrl) {
    results.push({
      json: {
        ...item.json,
        isValid: false,
        skipReason: "No PDF URL provided"
      }
    });
    continue;
  }

  // Extract filename from URL
  let fileName = "";
  try {
    const urlObj = new URL(pdfUrl);
    const pathParts = urlObj.pathname.split("/");
    fileName = pathParts[pathParts.length - 1] || "";

    // Decode URL-encoded characters
    fileName = decodeURIComponent(fileName);

    // If no filename or no extension, generate one
    if (!fileName || !fileName.includes(".")) {
      const timestamp = new Date().toISOString().replace(/[:.]/g, "-");
      fileName = "report-" + timestamp + ".pdf";
    }
  } catch (e) {
    const timestamp = new Date().toISOString().replace(/[:.]/g, "-");
    fileName = "report-" + timestamp + ".pdf";
  }

  // Validate it looks like a PDF URL
  const isPdfUrl = pdfUrl.toLowerCase().includes(".pdf") || 
                   pdfUrl.toLowerCase().includes("pdf") ||
                   pdfUrl.toLowerCase().includes("download");

  results.push({
    json: {
      ...item.json,
      pdfUrl: pdfUrl,
      fileName: fileName,
      isValid: true,
      isPdfUrl: isPdfUrl
    }
  });
}

return results;

// Extract metadata from uploaded file
const results = [];

for (const item of items) {
  const prepData = $('Prepare Download Info').item.json;
  const driveData = item.json;

  // Get file info from Google Drive response
  const fileId = driveData.id || "";
  const fileName = driveData.name || prepData.fileName || "unknown.pdf";
  const mimeType = driveData.mimeType || "application/pdf";
  const fileSize = driveData.size || 0;
  const webViewLink = driveData.webViewLink || "";
  const webContentLink = driveData.webContentLink || "";

  results.push({
    json: {
      pdfUrl: prepData.pdfUrl,
      title: prepData.Title || prepData.title || fileName.replace(".pdf", ""),
      source: prepData.Source || prepData.source || "Unknown",
      fileName: fileName,
      fileId: fileId,
      mimeType: mimeType,
      fileSize: fileSize,
      driveUrl: webViewLink || ("https://drive.google.com/file/d/" + fileId + "/view"),
      downloadUrl: webContentLink,
      downloadedAt: new Date().toISOString(),
      status: "Downloaded"
    }
  });
}

return results;
