// Extract and validate AI output
const results = [];

for (const item of items) {
  const output = item.json.output || item.json;
  const sourceData = $('Loop Over Sources').item.json;

  // Get values with fallbacks
  const source = output.source || sourceData.Source_Name || "Unknown";
  const title = output.title || "No title";
  const link = output.link || "";
  const fileType = output.file_type || "";
  const description = output.description || "";

  // Validate the result
  const isValid = link && 
                  link.startsWith("http") && 
                  title !== "No report found" &&
                  title !== "";

  // Determine status
  let status = "Discovered";
  if (!isValid) {
    status = "No Report Found";
  } else if (!link.includes(".pdf") && !link.includes(".xlsx") && !link.includes(".doc")) {
    status = "Link May Not Be Direct Download";
  }

  results.push({
    json: {
      source: source,
      title: title,
      link: link,
      fileType: fileType,
      description: description,
      sourceUrl: sourceData.Source_URL || "",
      category: sourceData.Category || "General",
      discoveredAt: new Date().toISOString(),
      status: status,
      isValid: isValid
    }
  });
}

return results;
