// LINE Webhook parsing
const body = items[0].json.body;

if (!body.events || !Array.isArray(body.events) || !body.events[0]) {
  throw new Error("No LINE events found");
}

const event = body.events[0];

return [
  {
    json: {
      replyToken: event.replyToken,
      userId: event.source.userId,
      message: event.message.text,
      timestamp: event.timestamp
    }
  }
];

const response = items[0].json;
let promptText = "";
let text = "";

if (response.candidates && response.candidates[0] && response.candidates[0].content && response.candidates[0].content.parts) {
  text = response.candidates[0].content.parts[0].text;
} 
else if (response.content && response.content.parts && response.content.parts[0]) {
  text = response.content.parts[0].text;
}
else if (response.output) {
  text = response.output;
}

if (text) {
  text = text.replace(/```json/g, "").replace(/```/g, "").trim();
  try {
    const parsed = JSON.parse(text);
    if (Array.isArray(parsed) && parsed.length > 0) {
      const firstItem = parsed[0];
      if (typeof firstItem === 'object' && firstItem !== null && firstItem.prompt) {
        promptText = firstItem.prompt; 
      } else if (typeof firstItem === 'string') {
        promptText = firstItem;
      } else {
        promptText = JSON.stringify(firstItem);
      }
    } else if (typeof parsed === 'object' && parsed !== null) {
       if (parsed.prompt) { promptText = parsed.prompt; } else { promptText = JSON.stringify(parsed); }
    } else {
      promptText = text; 
    }
  } catch (e) {
    promptText = text.replace(/^\[|\]$/g, '').replace(/^[\"']|[\"']$/g, '').replace(/\\n/g, '').trim();
  }
}

if (!promptText || typeof promptText !== 'string') { promptText = String(promptText || ""); }
promptText = promptText.replace(/\"/g, "'").replace(/\n/g, " ").replace(/\r/g, " ").replace(/\s+/g, " ").trim();

return [{
  json: {
    prompt: promptText,
    userId: $('Extract Data').item.json.userId || "unknown_user",
    taskId: response.taskId || null
  }
}];

// Parse response and extract image URL
const response = items[0].json;
const data = Array.isArray(response) ? response[0] : response;

// Asumimos que si llegamos aqui es porque el estado es success (verificado por el nodo IF)
if (data.data && data.data.resultJson) {
  try {
    const resultData = JSON.parse(data.data.resultJson);
    const resultUrls = resultData.resultUrls || [];
    if (resultUrls.length > 0) {
      return [{
        json: {
          imageUrl: resultUrls[0],
          taskId: data.data.taskId,
          status: 'completed'
        }
      }];
    }
  } catch (e) {
    throw new Error('Failed to parse resultJson');
  }
}

throw new Error('No image found in response');
