// Extract and parse JSON from AI Agent output
const input = $input.first();
const output = input.json.output || input.json.text || '';

// Extract JSON (```json ... ``` or { ... })
let jsonStr = output;
const jsonMatch = output.match(/```json\s*([\s\S]*?)```/) || output.match(/(\{[\s\S]*\})/);
if (jsonMatch) {
  jsonStr = jsonMatch[1];
}

let parsed;
try {
  parsed = JSON.parse(jsonStr.trim());
} catch (e) {
  // Default values on parse failure
  parsed = {
    translated_text: "Translation Error",
    sentiment: "Neutral",
    sentiment_score: 0,
    importance: "Low",
    category: "Other",
    key_phrase: "Parse Error"
  };
}

// Merge with original data
const originalData = $('Format Input Data').first().json;

return {
  json: {
    ...originalData,
    TranslatedText: parsed.translated_text,
    Sentiment: parsed.sentiment,
    SentimentScore: parsed.sentiment_score,
    Importance: parsed.importance,
    Category: parsed.category,
    KeyPhrase: parsed.key_phrase,
    ProcessStatus: "Completed",
    ProcessedAt: new Date().toISOString()
  }
};

