// Loop over input items and add a new field called 'myNewField' to the JSON of each one
const addresses = $input.all().map(item => `\`${item.json.token}\``);
let message =  "Your token addresses:\n" + addresses.join("\n")

$input.all()[0].json.message = message;

// If other items exist, they pass through untouched
return $input.all();

