const tz = "Asia/Kolkata";
const now = new Date().toLocaleString("en-IN", { timeZone: tz });

const articles = items.map(it => {
  const j = it.json;

  return `
    <div style="padding:16px; border:1px solid #eee; border-radius:8px; margin-bottom:20px; background-color:#f9f9f9;">
      <h3 style="margin-top:0; margin-bottom:8px;">
        <a href="${j.Link}" target="_blank" style="color:#0077cc; text-decoration:none;">
          ${j.Title}
        </a>
      </h3>
      <p style="margin:0 0 6px 0; font-size:13px; color:#888;">
        Published on: ${new Date(j.IsoDate).toLocaleDateString("en-IN", { day: 'numeric', month: 'short', year: 'numeric' })}
      </p>
      <p style="margin:0; font-size:15px; line-height:1.6; color:#333;">
        ${j.ContentSnippet || ''}
      </p>
    </div>
  `;
}).join("");

return [{
  json: {
    subject: `XYZ Marketing NewsLetter ‚Äì ${now}`,
    htmlBody: `
      <div style="font-family:'Segoe UI', Roboto, sans-serif; padding:24px; background-color:#ffffff; color:#333; max-width:700px; margin:auto;">
