const items = $input.all();
const hazardousAsteroids = [];
let biggestAsteroid = null;
let maxSize = 0;

for (const item of items) {
  const asteroid = item.json;
  const size = asteroid.estimated_diameter?.kilometers?.estimated_diameter_max || 0;
  const isHazardous = asteroid.is_potentially_hazardous_asteroid;

  if (isHazardous) {
    hazardousAsteroids.push({
      name: asteroid.name,
      id: asteroid.id,
      size_km: size,
      size_meters: size * 1000,
      velocity_kmh: parseFloat(asteroid.close_approach_data?.[0]?.relative_velocity?.kilometers_per_hour || 0),
      velocity_kms: parseFloat(asteroid.close_approach_data?.[0]?.relative_velocity?.kilometers_per_second || 0),
      miss_distance_km: parseFloat(asteroid.close_approach_data?.[0]?.miss_distance?.kilometers || 0),
      miss_distance_lunar: parseFloat(asteroid.close_approach_data?.[0]?.miss_distance?.lunar || 0),
      close_approach_date: asteroid.close_approach_data?.[0]?.close_approach_date_full,
      is_potentially_hazardous: true,
      nasa_url: asteroid.nasa_jpl_url,
      absolute_magnitude: asteroid.absolute_magnitude_h
    });
  }

  if (size > maxSize) {
    maxSize = size;
    biggestAsteroid = {
      name: asteroid.name,
      id: asteroid.id,
      size_km: size,
      size_meters: size * 1000,
      velocity_kmh: parseFloat(asteroid.close_approach_data?.[0]?.relative_velocity?.kilometers_per_hour || 0),
      velocity_kms: parseFloat(asteroid.close_approach_data?.[0]?.relative_velocity?.kilometers_per_second || 0),
      miss_distance_km: parseFloat(asteroid.close_approach_data?.[0]?.miss_distance?.kilometers || 0),
      miss_distance_lunar: parseFloat(asteroid.close_approach_data?.[0]?.miss_distance?.lunar || 0),
      close_approach_date: asteroid.close_approach_data?.[0]?.close_approach_date_full,
      is_potentially_hazardous: asteroid.is_potentially_hazardous_asteroid,
      nasa_url: asteroid.nasa_jpl_url,
      absolute_magnitude: asteroid.absolute_magnitude_h
    };
  }
}

let alertLevel = 'LOW';
let alertColor = '#36a64f';

if (hazardousAsteroids.length > 0) {
  alertLevel = 'MEDIUM';
  alertColor = '#FFA500';

  const veryClose = hazardousAsteroids.filter(a => a.miss_distance_lunar < 10);
  if (veryClose.length > 0) {
    alertLevel = 'HIGH';
    alertColor = '#FF0000';
  }
}

return [{
  json: {
    biggest_asteroid: biggestAsteroid,
    hazardous_asteroids: hazardousAsteroids,
    hazardous_count: hazardousAsteroids.length,
    total_asteroids: items.length,
    alert_level: alertLevel,
    alert_color: alertColor,
    scan_date: new Date().toISOString(),
    scan_range_days: 7
  }
}];

const data = $input.all()[0].json;
const asteroidName = data.hazardous_asteroids[0]?.name || data.biggest_asteroid?.name;

return [
  { json: { ...data, lang: 'en', country: 'us', query_suffix: ' asteroid news threat', search_name: asteroidName } },
  { json: { ...data, lang: 'ja', country: 'jp', query_suffix: ' Â∞èÊÉëÊòü Âç±Èô∫ „Éã„É•„Éº„Çπ', search_name: asteroidName } },
  { json: { ...data, lang: 'en', country: 'gb', query_suffix: ' asteroid news ESA', search_name: asteroidName } }
];

const items = $input.all();
let text = items[0].json.output?.[0]?.content?.[0]?.text || items[0].json.message?.content || '';

if (!text && items[0].json.text) {
  text = items[0].json.text;
}

let jsonString = text;
if (text.includes('```')) {
  const match = text.match(/```(?:json)?\s*([\s\S]*?)```/);
  if (match) {
    jsonString = match[1];
  }
}

try {
  const parsed = JSON.parse(jsonString.trim());
  return [{ json: parsed }];
} catch (e) {
  return [{ json: {
    threat_score: 0,
    media_panic_score: 0,
    fact_check_results: [],
    regional_comparison: {
      us_tone: 'unknown',
      japan_tone: 'unknown', 
      eu_tone: 'unknown',
      most_accurate_region: 'unknown',
      analysis: 'Unable to parse AI response'
    },
    misinformation_detected: false,
    misinformation_details: '',
    official_threat_summary: 'Analysis failed - please check manually',
    recommendation: 'Manual review required',
    parse_error: e.message,
    raw_response: text
  }}];
}

