  type: 'gauge',
  data: {
    datasets: [{
      value: threatScore,
      data: [30, 60, 100],
      backgroundColor: ['#36a64f', '#FFA500', '#FF0000']
    }]
  },
  options: {
    title: {
      display: true,
      text: 'Actual Threat Level (NASA Data)'
    }
  }
};

const panicChartConfig = {
  type: 'gauge',
  data: {
    datasets: [{
