    }]
  },
  options: {
    title: {
      display: true,
      text: 'Media Panic Level'
    }
  }
};

const toneToNumber = (tone) => {
  const map = { 'calm': 25, 'cautious': 50, 'alarming': 75, 'sensational': 100 };
  return map[tone] || 0;
};

const regionalChartConfig = {
  type: 'bar',
  data: {
    labels: ['US', 'Japan', 'EU'],
    datasets: [{
      label: 'Sensationalism Level',
      data: [
        toneToNumber(analysisData.regional_comparison?.us_tone),
        toneToNumber(analysisData.regional_comparison?.japan_tone),
        toneToNumber(analysisData.regional_comparison?.eu_tone)
      ],
      backgroundColor: ['#4285F4', '#EA4335', '#34A853']
    }]
  },
  options: {
    scales: { y: { max: 100, min: 0 } },
    title: { display: true, text: 'Regional Media Tone Comparison' }
  }
};

const threatChartUrl = 'https://quickchart.io/chart?c=' + encodeURIComponent(JSON.stringify(threatChartConfig));
const panicChartUrl = 'https://quickchart.io/chart?c=' + encodeURIComponent(JSON.stringify(panicChartConfig));
const regionalChartUrl = 'https://quickchart.io/chart?c=' + encodeURIComponent(JSON.stringify(regionalChartConfig));

return [{ 
  json: { 
    threat_chart_url: threatChartUrl,
    panic_chart_url: panicChartUrl,
    regional_chart_url: regionalChartUrl,
