const msgs = $json.messages.data;

// Sort messages latest ‚Üí oldest
msgs.sort((a, b) => new Date(b.created_time) - new Date(a.created_time));

// Latest message is msgs[0]
return [{ json: msgs[0] }];

// Input is an array of merged items
const items = $input.all();

// 1) Extract and parse the AI output (first item)
let aiRaw = items[0].json.output || "{}";
let aiParsed = {};

try {
  aiParsed = JSON.parse(aiRaw);
} catch (err) {
  aiParsed = { error: "AI output JSON parsing failed", raw: aiRaw };
}

// 2) Extract all Airtable records (everything except index 0)
let inventoryRecords = items.slice(1).map(i => i.json);

// 3) Return a single combined object
return [
  {
    json: {
      ai: aiParsed,
      inventory: inventoryRecords
    }
  }
];

// Get raw string output from the AI node
const raw = $json.output;

let parsed;

try {
  // Parse the JSON string into an object
  parsed = JSON.parse(raw);
} catch (err) {
  throw new Error("Failed to parse AI output JSON: " + err.message);
}

// Return parsed JSON as a clean object for next nodes
return [
  { json: parsed }
];
