const keywords = [
  "–∫–∞–∫", "—Å–∫–æ–ª—å–∫–æ", "–∫–æ–≥–¥–∞", "–≤–æ —Å–∫–æ–ª—å–∫–æ", "–∫—É–¥–∞", "–ø–æ—á–µ–º—É", "–Ω—É–∂–Ω–æ", "–º–æ–∂–Ω–æ",
  "—á—Ç–æ –¥–µ–ª–∞—Ç—å", "–≥–¥–µ", "–∫—Ç–æ", "–∑–∞—á–µ–º", "–æ–±—è–∑–∞–Ω", "–Ω–∞–¥–æ", "—Å—Ç–æ–∏—Ç", "–¥–æ–ª–∂–µ–Ω",
  "–±—ã–≤–∞—é—Ç", "—Å –∫–µ–º", "–¥–ª—è —á–µ–≥–æ", "–µ—Å—Ç—å –ª–∏", "—É –∫–æ–≥–æ", "–∫–æ–º—É", "–∫—É–¥–∞ –æ–±—Ä–∞—Ç–∏—Ç—å—Å—è",
  "—á—Ç–æ —ç—Ç–æ", "–∫–∞–∫ –±—ã—Å—Ç—Ä–æ"
];

// –¢–µ–∫—Å—Ç —Å–æ–æ–±—â–µ–Ω–∏—è
const text = $json?.message?.text || $json?.text || "";

// –ø–æ –∫–ª—é—á–µ–≤—ã–º —Å–ª–æ–≤–∞–º
let matchedByKeyword = false;
for (let word of keywords) {
  if (text.toLowerCase().includes(word)) {
    matchedByKeyword = true;
    break;
  }
}

// –ø–æ –∑–Ω–∞–∫—É –≤–æ–ø—Ä–æ—Å–∞
const matchedByQuestionMark = text.includes("?");

// –∏—Ç–æ–≥–æ–≤—ã–π —Ñ–ª–∞–≥: –≤–æ–ø—Ä–æ—Å, –µ—Å–ª–∏ –µ—Å—Ç—å –∫–ª—é—á–µ–≤–æ–µ —Å–ª–æ–≤–æ –ò–õ–ò –∑–Ω–∞–∫ –≤–æ–ø—Ä–æ—Å–∞
const matched = matchedByKeyword || matchedByQuestionMark;

// –∫–æ–ª-–≤–æ —Å–ª–æ–≤
const wordsCount = text.trim() ? text.trim().split(/\s+/).length : 0;

return [{
  json: {
    ...$json,
    keywordMatch: matched,
    matchedByKeyword,
    matchedByQuestionMark,
    wordsCount
  }
}];

return [{
  json: {
    question_id: $json.question_message_id || ($json.message && $json.message.message_id) || null,
    chat_id: $json.group_id || $json.chat_id || ($json.message && $json.message.chat && $json.message.chat.id) || null,
    user_id: $json.user_id || ($json.message && $json.message.from && $json.message.from.id) || null,
    user_name: $json.username || ($json.message && $json.message.from && $json.message.from.username) || null,
    question_text: $json.question || ($json.message && $json.message.text) || null,

    // —Å—Ç–∞—Ä–æ–µ –ø–æ–ª–µ –∏–∑ –≤—Ö–æ–¥–∞
    group_message_id: $json.group_message_id || null,

    // –ù–û–í–û–ï: –æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω—ã–π ID —Å–æ–æ–±—â–µ–Ω–∏—è –∏–∑ Set Original Question
    original_group_message_id: $json.original_group_message_id || ($json.message && $json.message.message_id) || null,

    asked_at: new Date().toISOString(),
    status: "sent_to_expert"
  }
}];

// –í–ø–∏—à–∏ user_id –≤—Å–µ—Ö —ç–∫—Å–ø–µ—Ä—Ç–æ–≤. –¢–≤–æ–π id —É–∑–Ω–∞—ë—à—å –∏–∑ –ª—é–±–æ–≥–æ —Å–æ–æ–±—â–µ–Ω–∏—è –≤ Telegram —á–µ—Ä–µ–∑ n8n.
const experts = [
    123456789,   // ‚Üê –∑–∞–º–µ–Ω–∏ –Ω–∞ —Å–≤–æ–π id, –ø–æ–∫–∞ —Ç–µ—Å—Ç–∏—Ä—É–µ—à—å
    // –º–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å –¥—Ä—É–≥–∏—Ö —ç–∫—Å–ø–µ—Ä—Ç–æ–≤
];

const userId = $json["message"]["from"]["id"];

return [{
    json: {
        isExpert: experts.includes(userId),
        ...$json
    }
}];

const replyText = $json.message.reply_to_message?.text || "";
// –ò—â–µ–º Q:XXX... –≤ —Ç–µ–∫—Å—Ç–µ (—É–Ω–∏–≤–µ—Ä—Å–∞–ª—å–Ω–æ: –ª—é–±—ã–µ —Å–∏–º–≤–æ–ª—ã –∫—Ä–æ–º–µ –ø—Ä–æ–±–µ–ª–∞)
const match = replyText.match(/Q:([^\s]+)/);
const questionId = match ? match[1] : null;

return [{
  json: {
    reply_flag: Boolean($json.message.reply_to_message),
    question_id_from_reply: questionId,
    ...$json
  }
}]

// –ë–µ—Ä—ë–º –ø–µ—Ä–≤—ã–π –¥–æ–∫—É–º–µ–Ω—Ç –∏–∑ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤ Pinecone
const doc = $json.result ? $json.result[0] : $json; // –µ—Å–ª–∏ Pinecone –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç –º–∞—Å—Å–∏–≤ result
return [{
  json: {
    question: doc.metadata?.question,
    answer: doc.metadata?.answer,
    group_id: doc.metadata?.group_id,
    question_message_id: doc.metadata?.question_message_id,
    ...doc.metadata, // –µ—Å–ª–∏ –º–Ω–æ–≥–æ –ø–æ–ª–µ–π
  }
}]

const originalQuestion =
  items[0].json.original_question ||
  items[0].json.question ||
  "–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ";

const chatId =
  items[0].json.chat_id ||
  items[0].json.document?.metadata?.chat_id ||
  items[0].json.document?.metadata?.group_id ||
  items[0].json.group_id ||
  "";

const replyToMessageId =
  items[0].json.reply_to_message_id ||
  items[0].json.message_id ||
  items[0].json.document?.metadata?.question_message_id ||
  "";

const userId = items[0].json.userid || items[0].json.user_id || "";

// –ë–µ—Ä—ë–º group_message_id –∏–∑ –≤—Ö–æ–¥–Ω–æ–≥–æ –¥–æ–∫—É–º–µ–Ω—Ç–∞, –µ—Å–ª–∏ –µ—Å—Ç—å
const groupMessageId =
  items[0].json.document?.metadata?.group_message_id || "";

// –ú–∏–Ω–∏–º–∞–ª—å–Ω—ã–π –ø–æ—Ä–æ–≥
const MIN_SCORE_THRESHOLD = 0.90;

// –ò—â–µ–º item —Å –æ—Ç–≤–µ—Ç–æ–º
const found = items.find(item => {
  const j = item.json || {};
  const meta = j.document?.metadata || {};

  const answer = meta.answer || j.answer || "";
  const score = j.score || 0;
  const keywordMatch = j.keywordMatch;

  const hasAnswer = !!answer && answer.trim() !== "";
  const keywordOk = (typeof keywordMatch === "boolean") ? keywordMatch : true;

  return hasAnswer && keywordOk && score >= MIN_SCORE_THRESHOLD;
});

if (found) {
  const j = found.json || {};
  const meta = j.document?.metadata || {};

  const foundAnswer =
    meta.answer ||
    j.answer ||
    "";

  return [{
    json: {
      ...j,
      chat_id: chatId,
      answer: foundAnswer,
      text: foundAnswer,
      reply_to_message_id: replyToMessageId,
      group_message_id: groupMessageId,
      original_message_id: items[0].json.original_message_id || "",
      has_answer: true,
      score: typeof j.score !== "undefined" ? j.score : 0,
      user_name:
        meta.username ||
        meta.user_name ||
        j.username ||
        j.user_name ||
        "",
      user_id: userId
    }
  }];
} else {
  const backup = items[0].json;

  return [{
    json: {
      chat_id: chatId,
      text: "",
      reply_to_message_id: replyToMessageId,
      group_message_id: groupMessageId,
      question: originalQuestion,
      original_question: originalQuestion,
      original_message_id: items[0].json.original_message_id || "",
      has_answer: false,
      answer: "",
      score: typeof backup.score !== "undefined" ? backup.score : 0,
      user_name:
        backup.document?.metadata?.username ||
        backup.document?.metadata?.user_name ||
        backup.username ||
        backup.user_name ||
        "",
      user_id: userId,
      send_to_expert: true
    }
  }];
}

// –ï—Å–ª–∏ Pinecone –≤–µ—Ä–Ω—É–ª –ø—É—Å—Ç–æ ‚Äî –≤—ã—Ö–æ–¥–∏–º —Å –ø—É—Å—Ç—ã–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–º
if (!items || items.length === 0) {
  return [{ json: { empty: true, items: [] } }];
}

const threshold = 0.85;

items.forEach(item => {
  // –í–æ–ø—Ä–æ—Å, –∫–æ—Ç–æ—Ä—ã–π –∑–∞–¥–∞–ª –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å
  const inputQuestion =
    item.json.original_question ||
    item.json.message?.text ||
    item.json.document?.metadata?.question_text ||
    "";

  const score = item.json.score || 0;

  // –ï—Å—Ç—å –ª–∏ –æ—Ç–≤–µ—Ç –≤ –¥–æ–∫—É–º–µ–Ω—Ç–µ
  const hasAnswer =
    !!item.json.document?.metadata?.answer &&
    item.json.document.metadata.answer.trim() !== "";

  // –í–æ–ø—Ä–æ—Å –∏–∑ –¥–æ–∫—É–º–µ–Ω—Ç–∞
  const question =
    item.json.document?.metadata?.question_text ||
    item.json.document?.pageContent ||
    "";

  // –ö–ª—é—á–µ–≤–∞—è —Ñ—Ä–∞–∑–∞ –∏–∑ –≤—Ö–æ–¥–Ω–æ–≥–æ –≤–æ–ø—Ä–æ—Å–∞
  const keyPhrase = inputQuestion.toLowerCase().slice(0, 5);

  // –ù–∞—Å–∫–æ–ª—å–∫–æ –¥–æ–∫—É–º–µ–Ω—Ç —Ä–µ–ª–µ–≤–∞–Ω—Ç–µ–Ω
  const isRelevant = question.toLowerCase().includes(keyPhrase);

  // –î–æ–±–∞–≤–ª—è–µ–º –æ—Ç–ª–∞–¥–æ—á–Ω—É—é –∏–Ω—Ñ—É
  item.json.debug = { question, score, hasAnswer, isRelevant, inputQuestion };

  // –í–ê–ñ–ù–û: –Ω–µ –∑–∞—Ç–∏—Ä–∞–µ–º metadata, –∞ —Ç–æ–ª—å–∫–æ –¥–æ–±–∞–≤–ª—è–µ–º —Å–≤–æ–∏ —Ñ–ª–∞–≥–∏
  if (item.json.document && item.json.document.metadata) {
    item.json.document.metadata = {
      ...item.json.document.metadata,
      hasAnswer,
      isRelevant
    };
  }
});

return items;

const inputData = $input.all()[0].json;
const pineconeResult = inputData.result || [];
const currentQuestion = inputData.question;

let cacheHit = false;
let cachedAnswer = null;
let score = 0; // –æ–±—ä—è–≤–ª—è–µ–º –∑–¥–µ—Å—å

if (pineconeResult.length > 0) {
  const bestMatch = pineconeResult[0];
  score = bestMatch.score || bestMatch.metadata?.score || 0;
  if (score > 0.1) {
    cacheHit = true;
    cachedAnswer = '–û—Ç–≤–µ—Ç –∏–∑ –∫—ç—à–∞: ' + (bestMatch.metadata?.pageContent || bestMatch.pageContent || bestMatch.answer || '–ù–µ—Ç —Ç–µ–∫—Å—Ç–∞');
  }
}

return [{ json: { ...inputData, cacheHit, cachedAnswer, debugScore: score } }];

const questionText = $json.message?.reply_to_message?.text || "";

// –†–∞–∑–±–∏–≤–∞–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –Ω–∞ —Å—Ç—Ä–æ–∫–∏
const lines = questionText.split('\n\n');
let originalQuestion = "–≤–æ–ø—Ä–æ—Å –Ω–µ –Ω–∞–π–¥–µ–Ω";
let questionIdFromReply = null;

// –ë–µ—Ä—ë–º —Ç–µ–∫—Å—Ç –≤–æ–ø—Ä–æ—Å–∞ (–≤—Ç–æ—Ä–∞—è —Å–µ–∫—Ü–∏—è)
if (lines.length >= 2) {
  originalQuestion = lines[1];
}

// –ü–∞—Ä—Å–∏–º ID (Q:XXXX...) –ª—é–±–æ–π –¥–ª–∏–Ω—ã
const match = questionText.match(/Q:([^\s]+)/);
if (match) {
  questionIdFromReply = match[1];
}

// –û—Ç–≤–µ—Ç —ç–∫—Å–ø–µ—Ä—Ç–∞
const expertAnswer = $json.message?.text || "";

// Username (–µ—Å–ª–∏ –µ—Å—Ç—å)
const username = $json.message?.from?.username || "";

return [{
  json: {
    question: originalQuestion,
    answer: expertAnswer,
    reply_message_id: $json.reply_message_id,
    chat_id: "",
    user_id: $json.user_id,
    username: username,
    question_id_from_reply: questionIdFromReply
  }
}];

return {
  json: {
    question: $json.question,
    answer: $json.answer,
    user_id: $json.user_id,
    group_id: $json.group_id,
    question_message_id: $json.question_message_id,
    username: $json.username,
    pageContent: $json.question
  }
};
