return items.map(item => {
  // 1Ô∏è‚É£ Google Sheet values
  const campaign = item.json["Campaign"] || "N/A";
  const subject = item.json["Subject"] || "N/A";
  const audience = item.json["Audience"] || "N/A";

  // 2Ô∏è‚É£ Tavily results
  let tavilyResults = "";
  if (Array.isArray(item.json.results) && item.json.results.length > 0) {
    tavilyResults = item.json.results
      .map((r, i) => {
        const title = r.title || `Result ${i + 1}`;
        const content = r.content || r.text || r.snippet || "";
        return `‚Ä¢ ${title}: ${content}`;
      })
      .filter(Boolean)
      .join("\n");
  } else {
    tavilyResults = "No relevant posts found.";
  }

  // 3Ô∏è‚É£ Create chatInput for AI Agent
  item.json.chatInput = `
Campaign: ${campaign}
Subject: ${subject}
Audience: ${audience}

Tavily search results:
${tavilyResults}

Please generate a professional and engaging LinkedIn post for this campaign.
Include:
- Hook (1-2 lines)
- Key insights (2-3 sentences or bullet points)
- Call-to-action (CTA)
Make it ready to publish and include emojis relevant to the campaign, subject, and audience.
  `.trim();

  return item;
});

// Process multiple items and return only the desired fields
return items.map(item => {
  return {
    json: {
      Campaign: item.json.Campaign,
      Subject: item.json.Subject,
      Audience: item.json.Audience,
      rowNumber: item.json.ID  // use ID from trigger as row number
    }
  };
});

