parameters.url:
={{
(() => {
  // 1. Trim whitespace (Crucial first step)
  let url = $json.Website.trim();

  // 2. Add protocol if missing (Default to https)
  if (!url.match(/^https?:\/\//)) {
    url = 'https://' + url;
  }

  try {
    // 3. Parse the URL object
    const urlObj = new URL(url);

    // 4. Add 'www.' ONLY if:
    //    a) It is missing
    //    b) It looks like a root domain (has exactly 2 parts, e.g., 'harvex-ai.com')
    //    This prevents breaking subdomains like 'docs.google.com'
    if (!urlObj.hostname.startsWith('www.') && urlObj.hostname.split('.').length === 2) {
      urlObj.hostname = 'www.' + urlObj.hostname;
    }

    // 5. Return the clean URL
    return urlObj.toString();

  } catch (error) {
    // If the input is total garbage, return original to let the node error out naturally
    return url;
  }
})()
}}

assignments.value:
={{
(() => {
  const extractedPath = $json.output[0].content[0].text.match(/"fallback_url":\s*"([^"]+)"/)[1];


  const rawBase = $('On form submission').item.json.Website;


  const cleanBase = (rawBase.startsWith('http') ? rawBase : 'https://' + rawBase).replace(/\/$/, "");

  // 4. Combine them
  return extractedPath.startsWith('http') ? extractedPath : cleanBase + extractedPath;
})()
}}
