// This code will run for each item coming from the AI Agent.
const item = $input.item;
const aiOutput = item.json.output;

// Use a try-catch block to safely handle potential JSON parsing errors
try {
  // Regular expression to find a JSON object within the AI's output string.
  const jsonMatch = aiOutput.match(/{.*}/s);

  if (!jsonMatch) {
    throw new Error("No JSON object found in the AI output.");
  }

  const jsonString = jsonMatch[0];
  const parsedData = JSON.parse(jsonString);

  // --- RESILIENT KEY CHECKING ---
  // This is the important part. It checks for multiple possible key names.
  const title = parsedData.blogTitle || parsedData.title;
  const content = parsedData.blogContent || parsedData.description;

  // If either the title or content could not be found, throw an error.
  if (!title || !content) {
    throw new Error("The parsed JSON is missing a recognizable title or content field.");
  }

  // Return the data using the consistent keys that the next nodes expect.
  return {
    json: {
      ...item.json, // Carry over original data
      blogTitle: title,
      blogContent: content
    }
  };

} catch (error) {
  // If anything goes wrong, log the error and prevent this item from proceeding.
  console.error("Failed to parse AI output:", error.message, "Raw Output:", aiOutput);
  return {
    json: {
      error: "Failed to parse AI output",
      errorMessage: error.message
    }
  };
}

