const aiResponse = $input.first().json.message;
let parsedData;

try {
  parsedData = typeof aiResponse === 'string' ? JSON.parse(aiResponse) : aiResponse;
} catch (e) {
  const scoreMatch = aiResponse.match(/"score"\s*:\s*(\d+)/);
  const categoryMatch = aiResponse.match(/"category"\s*:\s*"([^"]+)"/);
  const confidenceMatch = aiResponse.match(/"confidence"\s*:\s*"([^"]+)"/);

  parsedData = {
    score: scoreMatch ? parseInt(scoreMatch[1]) : 50,
    category: categoryMatch ? categoryMatch[1] : 'Warm',
    confidence: confidenceMatch ? confidenceMatch[1] : 'medium',
    reasoning: 'Parsed from text response'
  };
}

const score = Math.max(1, Math.min(100, parseInt(parsedData.score) || 50));

return {
  json: {
    score: score,
    category: parsedData.category || 'Warm',
    confidence: parsedData.confidence || 'medium',
    reasoning: parsedData.reasoning || 'No reasoning provided',
    contactId: $('Clean Contact Data').first().json.contactId,
    name: $('Clean Contact Data').first().json.name,
    email: $('Clean Contact Data').first().json.email,
    source: $('Clean Contact Data').first().json.source
  }
};

