const items = $input.all();
const output = [];

for (const item of items) {
  const aiResponse = JSON.parse(item.json.message.content);
  const originalData = item.json;

  // Determine category based on keywords
  const text = originalData.text ? originalData.text.toLowerCase() : '';
  let category = 'general';

  if (text.includes('service') || text.includes('staff') || text.includes('employee')) {
    category = 'service';
  } else if (text.includes('price') || text.includes('cost') || text.includes('expensive')) {
    category = 'price';
  } else if (text.includes('product') || text.includes('quality') || text.includes('food')) {
    category = 'product';
  } else if (text.includes('experience') || text.includes('atmosphere') || text.includes('ambiance')) {
    category = 'experience';
  }

  output.push({
    json: {
      platform: originalData.platform,
      rating: originalData.rating,
      text: originalData.text,
      created_at: originalData.created_at,
      reviewer_name: originalData.reviewer_name,
      review_url: originalData.review_url,
      location: originalData.location,
      sentiment: aiResponse.sentiment,
      sentiment_score: aiResponse.sentiment_score,
      category: category,
      is_negative: aiResponse.sentiment === 'negative',
      main_issues: aiResponse.main_issues || [],
      main_highlights: aiResponse.main_highlights || [],
      suggested_response_tone: aiResponse.suggested_response_tone,
      summary: aiResponse.summary
    }
  });
}

return output;

