const items = $input.all();
const data = items[0].json;

// Count reviews per platform
const platformCounts = {};
data.platform.forEach(p => {
  platformCounts[p] = (platformCounts[p] || 0) + 1;
});

// Calculate sentiment breakdown
const sentimentCounts = { positive: 0, negative: 0, neutral: 0 };
data.sentiment.forEach(s => {
  sentimentCounts[s] = (sentimentCounts[s] || 0) + 1;
});

const totalReviews = data.sentiment.length;
const sentimentPercentages = {
  positive: ((sentimentCounts.positive / totalReviews) * 100).toFixed(1),
  negative: ((sentimentCounts.negative / totalReviews) * 100).toFixed(1),
  neutral: ((sentimentCounts.neutral / totalReviews) * 100).toFixed(1)
};

// Extract top issues and highlights
const allIssues = data.main_issues.flat().filter(i => i);
const allHighlights = data.main_highlights.flat().filter(h => h);

const issueFrequency = {};
allIssues.forEach(issue => {
  issueFrequency[issue] = (issueFrequency[issue] || 0) + 1;
});

const highlightFrequency = {};
allHighlights.forEach(highlight => {
  highlightFrequency[highlight] = (highlightFrequency[highlight] || 0) + 1;
});

const topIssues = Object.entries(issueFrequency)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 5)
  .map(([issue, count]) => `${issue} (${count} mentions)`);

const topHighlights = Object.entries(highlightFrequency)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 5)
  .map(([highlight, count]) => `${highlight} (${count} mentions)`);

return [{
  json: {
    totalReviews,
    platformCounts,
    sentimentCounts,
    sentimentPercentages,
    topIssues,
    topHighlights
  }
}];

const items = $input.all();
const output = [];

for (const item of items) {
  const data = item.json;
  let normalized = {};

  // Detect platform and normalize accordingly
  if (data.starRating !== undefined && data.reviewer && data.reviewer.displayName) {
    // Google Review
    normalized = {
      platform: 'Google',
      rating: data.starRating,
      text: data.comment || '',
      created_at: data.createTime || '',
      reviewer_name: data.reviewer.displayName || '',
      review_url: data.name ? `https://www.google.com/maps/reviews/${data.name}` : '',
      location: data.name || ''
    };
  } else if (data.rating !== undefined && data.review_text !== undefined && data.reviewer) {
    // Facebook Review
    normalized = {
      platform: 'Facebook',
      rating: data.rating,
      text: data.review_text || '',
      created_at: data.created_time || '',
      reviewer_name: data.reviewer.name || '',
      review_url: data.open_graph_story ? data.open_graph_story.id : '',
      location: data.page ? data.page.name : ''
    };
  } else if (data.stars !== undefined && data.text !== undefined && data.consumer) {
    // Trustpilot Review
    normalized = {
      platform: 'Trustpilot',
      rating: data.stars,
      text: data.text || '',
      created_at: data.createdAt || '',
      reviewer_name: data.consumer.displayName || '',
      review_url: data.links ? data.links[0].href : '',
      location: data.businessUnit ? data.businessUnit.displayName : ''
    };
  } else if (data.rating !== undefined && data.text !== undefined && data.user) {
    // Yelp Review
    normalized = {
      platform: 'Yelp',
      rating: data.rating,
      text: data.text || '',
      created_at: data.time_created || '',
      reviewer_name: data.user.name || '',
      review_url: data.url || '',
      location: data.business ? data.business.name : ''
    };
  } else {
    // Unknown format - pass through with generic mapping
    normalized = {
      platform: data.platform || 'Unknown',
      rating: data.rating || 0,
      text: data.text || '',
      created_at: data.created_at || '',
      reviewer_name: data.reviewer_name || '',
      review_url: data.review_url || '',
      location: data.location || ''
    };
  }

  output.push({ json: normalized });
}

return output;
