const minViews = $('Workflow Configuration').first().json.minViews;
const minEngagementRate = $('Workflow Configuration').first().json.minEngagementRate;
const maxAgeHours = $('Workflow Configuration').first().json.maxAgeHours;

const filtered = $input.all().filter(item => {
  const data = item.json;
  const views = data.views || 0;
  const likes = data.likes || 0;
  const comments = data.comments || 0;
  const shares = data.shares || 0;

  // Calculate engagement rate
  const engagementRate = views > 0 ? (likes + comments + shares) / views : 0;

  // Calculate age in hours
  const postedAt = new Date(data.posted_at);
  const now = new Date();
  const ageHours = (now - postedAt) / (1000 * 60 * 60);

  // Apply filters
  return views >= minViews && 
         ageHours <= maxAgeHours && 
         engagementRate >= minEngagementRate;
}).map(item => {
  // Add calculated fields
  const data = item.json;
  const views = data.views || 0;
  const likes = data.likes || 0;
  const comments = data.comments || 0;
  const shares = data.shares || 0;
  const engagementRate = views > 0 ? (likes + comments + shares) / views : 0;

  const postedAt = new Date(data.posted_at);
  const now = new Date();
  const ageHours = (now - postedAt) / (1000 * 60 * 60);

  return {
    json: {
      ...data,
      engagement_rate: engagementRate,
      age_hours: ageHours
    }
  };
});

return filtered;

