// Transform aggregated data into email-friendly format
const videos = $input.all();

// Sort by engagement rate and growth score
const sortedVideos = videos.map(item => {
  const data = item.json;
  const engagementRate = data.engagement_rate || 0;
  const ageHours = data.age_hours || 1;

  // Calculate growth score (engagement rate / age in hours)
  const growthScore = Math.round((engagementRate / ageHours) * 10000) / 100;

  // Extract AI analysis data from agent node outputs
  // Agent nodes store their output in $json.output as a JSON string or object
  let visualAnalysis = {};
  let trendAnalysis = {};

  try {
    // Try to parse visual analysis output
    if (data.visual_analysis) {
      visualAnalysis = typeof data.visual_analysis === 'string' ? JSON.parse(data.visual_analysis) : data.visual_analysis;
    }
    // Try to parse trend analysis output
    if (data.trend_analysis) {
      trendAnalysis = typeof data.trend_analysis === 'string' ? JSON.parse(data.trend_analysis) : data.trend_analysis;
    }
  } catch (e) {
    console.log('Error parsing AI output:', e);
  }

  return {
    json: {
      platform: data.platform,
      videoUrl: data.video_url,
      author: data.author,
      views: data.views,
      likes: data.likes,
      comments: data.comments,
      shares: data.shares,
      engagementRate: Math.round(engagementRate * 10000) / 100,
      growthScore: growthScore,
      superViral: data.super_viral || false,
      visualHookAnalysis: visualAnalysis.visual_hook || 'N/A',
      mainEmotion: visualAnalysis.main_emotion || 'N/A',
      formattingPattern: visualAnalysis.formatting_pattern || 'N/A',
      trendSummary: trendAnalysis.summary || 'N/A',
      whyItWorks: trendAnalysis.why_it_works || 'N/A',
      trendType: trendAnalysis.trend_type || 'N/A',
      adaptationIdeas: Array.isArray(trendAnalysis.adaptation_ideas) ? trendAnalysis.adaptation_ideas.join('; ') : 'N/A',
      hookExamples: Array.isArray(trendAnalysis.hook_examples) ? trendAnalysis.hook_examples.join('; ') : 'N/A',
      postingAngles: Array.isArray(trendAnalysis.posting_angles) ? trendAnalysis.posting_angles.join('; ') : 'N/A'
    }
  };
}).sort((a, b) => b.json.growthScore - a.json.growthScore);

// Return top 5-10 videos
return sortedVideos.slice(0, 10);
