// Parse AI analysis result
const aiResponse = $input.first().json.text || $input.first().json.output;
let analysis = {};

try {
  // Extract JSON from response
  const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    analysis = JSON.parse(jsonMatch[0]);
  }
} catch (e) {
  analysis = {
    art_style: 'Unknown',
    color_palette: 'Unknown',
    mood: 'Unknown',
    technique: 'Unknown',
    subject: 'AI Generated Art',
    quality_score: 5,
    trending_potential: 'Medium',
    similar_artists: 'Unknown',
    japanese_description: 'AI„Ç¢„Éº„Éà‰ΩúÂìÅ'
  };
}

// Get original item data from loop
const originalData = $('Loop Over Items').item.json;

return {
  json: {
    ...originalData,
    ai_analysis: JSON.stringify(analysis),
    art_style: analysis.art_style,
    color_palette: analysis.color_palette,
    mood: analysis.mood,
    quality_score: analysis.quality_score,
    trending_potential: analysis.trending_potential,
    japanese_description: analysis.japanese_description,
    similar_artists: analysis.similar_artists
  }
};

// Filter posts from last 7 days and calculate statistics
const items = $input.all();
const now = new Date();
const weekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);

const weeklyPosts = items.filter(item => {
  const collectedAt = new Date(item.json.collected_at);
  return collectedAt >= weekAgo;
});

// Calculate statistics
const stats = {
  totalPosts: weeklyPosts.length,
  totalLikes: 0,
  totalComments: 0,
  avgEngagement: 0,
  styleBreakdown: {},
  moodBreakdown: {},
  colorBreakdown: {},
  topPosts: [],
  trendingStyles: [],
  peakDay: '',
  peakDayCount: 0
};

const dayCount = {};

weeklyPosts.forEach(post => {
  const data = post.json;
  stats.totalLikes += parseInt(data.likes) || 0;
  stats.totalComments += parseInt(data.comments) || 0;
  stats.avgEngagement += parseFloat(data.engagement_score) || 0;

  // Style breakdown
  if (data.art_style) {
    stats.styleBreakdown[data.art_style] = (stats.styleBreakdown[data.art_style] || 0) + 1;
  }

  // Color breakdown
  if (data.color_palette) {
    stats.colorBreakdown[data.color_palette] = (stats.colorBreakdown[data.color_palette] || 0) + 1;
  }

  // Day count
  const day = new Date(data.collected_at).toLocaleDateString('en-US', { weekday: 'long' });
  dayCount[day] = (dayCount[day] || 0) + 1;
});

stats.avgEngagement = weeklyPosts.length > 0 ? (stats.avgEngagement / weeklyPosts.length).toFixed(2) : 0;

// Find peak day
Object.entries(dayCount).forEach(([day, count]) => {
  if (count > stats.peakDayCount) {
    stats.peakDay = day;
    stats.peakDayCount = count;
  }
});

// Top 5 posts by engagement
stats.topPosts = weeklyPosts
  .sort((a, b) => (parseFloat(b.json.engagement_score) || 0) - (parseFloat(a.json.engagement_score) || 0))
  .slice(0, 5)
  .map(p => ({
    url: p.json.url,
    style: p.json.art_style,
    likes: p.json.likes,
    engagement: p.json.engagement_score,
    owner: p.json.owner_username
  }));

// Trending styles (sorted by count)
stats.trendingStyles = Object.entries(stats.styleBreakdown)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 5)
  .map(([style, count]) => ({ style, count, percentage: ((count / weeklyPosts.length) * 100).toFixed(1) }));

return { json: stats };

