const timestamp = Date.now();

// We must define the query string EXACTLY as it will be signed
// Order matters: size first, then timestamp
const queryString = `size=100&timestamp=${timestamp}`;

return {
  timestamp: timestamp,
  queryString: queryString
};

const capital = 10000; // Simulation amount
const results = [];

for (const product of $input.all()) {
  const apy = parseFloat(product.json.latestAnnualPercentageRate);
  results.push({
    asset: product.json.asset,
    apy_percent: (apy * 100).toFixed(2) + '%',
    daily_income: '$' + (capital * apy / 365).toFixed(2)
  });
}

return results;
