// Extract data from previous nodes
const calendarEvent = $('Get Next Calendar Event').first().json;
const travelData = $('Get Travel Time via Google Maps').first().json;
const config = $('Set Configuration').first().json;

// Get event start time
const eventStartTime = new Date(calendarEvent.start.dateTime || calendarEvent.start.date);

// Get travel duration in seconds from Google Maps API response
const travelDurationSeconds = travelData.routes?.[0]?.legs?.[0]?.duration?.value || 0;
const travelTimeMinutes = Math.ceil(travelDurationSeconds / 60);

// Calculate current time
const now = new Date();

// Calculate total available time until event (in minutes)
const totalAvailableMinutes = Math.floor((eventStartTime - now) / 1000 / 60);

// Calculate gap time (available time - travel time)
const gapTimeMinutes = totalAvailableMinutes - travelTimeMinutes;

// Get minimum gap time from config
const minGapTime = config.minGapTimeMinutes || 30;

// Determine if there's enough gap time
const hasGapTime = gapTimeMinutes >= minGapTime;

// Return the calculated data
return [{
  json: {
    eventStartTime: eventStartTime.toISOString(),
    travelTimeMinutes: travelTimeMinutes,
    totalAvailableMinutes: totalAvailableMinutes,
    gapTimeMinutes: gapTimeMinutes,
    minGapTimeMinutes: minGapTime,
    hasGapTime: hasGapTime,
    currentTime: now.toISOString(),
    eventLocation: calendarEvent.location,
    eventSummary: calendarEvent.summary
  }
}];

// Merge all context data from previous nodes
const gapTimeData = $('Calculate Available Gap Time').first().json;
const weatherData = $('Get Weather at Destination').first().json;
const userPreferences = $('Get User Preferences from Notion').all().map(item => item.json);
const calendarEvent = $('Get Next Calendar Event').first().json;
const config = $('Set Configuration').first().json;

// Extract weather ID for routing decision
const weatherId = weatherData.weather?.[0]?.id || 800;
const weatherMain = weatherData.weather?.[0]?.main || 'Clear';
const weatherDescription = weatherData.weather?.[0]?.description || '';

// Return merged context
return [{
  json: {
    // Gap time information
    gapTimeMinutes: gapTimeData.gapTimeMinutes,
    travelTimeMinutes: gapTimeData.travelTimeMinutes,
    eventStartTime: gapTimeData.eventStartTime,
    eventLocation: gapTimeData.eventLocation,
    eventSummary: gapTimeData.eventSummary,

    // Weather information
    weatherId: weatherId,
    weatherMain: weatherMain,
    weatherDescription: weatherDescription,
    weatherData: weatherData,

    // User preferences
    preferences: userPreferences,

    // Configuration
    currentLocation: config.currentLocation,
    googlePlacesApiKey: config.googlePlacesApiKey
  }
}];
