let txt = $json.text;

txt = txt.replace(/^```(?:json)?\s*|\s*```$/g, '').trim();

let parsed;

try {
  parsed = JSON.parse(txt);
} catch (e1) {
  try {
    parsed = eval('(' + txt + ')');
  } catch (e2) {
    throw new Error("Parsing failed! Cleaned value: " + txt);
  }
}

return [
  {
    json: {
      title: parsed.title,
      description: parsed.description,
      hashtags: parsed.hashtags,
    }
  }
];
