// Get the current item's HTML
let html = $('Get Website Content').item.json.data || '';

// 1: Remove Scripts and Styles
html = html.replace(/<script[\s\S]*?>[\s\S]*?<\/script>/gi, "");
html = html.replace(/<style[\s\S]*?>[\s\S]*?<\/style>/gi, "");

// 2: Remove Comments
html = html.replace(/<!--[\s\S]*?-->/g, "");

// 3: Remove all other HTML tags
html = html.replace(/<[^>]+>/g, " ");

// 4: Decode HTML entities
html = html
  .replace(/&nbsp;/g, " ")
  .replace(/&amp;/g, "&")
  .replace(/&quot;/g, '"')
  .replace(/&lt;/g, "<")
  .replace(/&gt;/g, ">");

// 5: Clean up excessive whitespace
// First: Convert carriage returns and tabs to newlines/spaces
html = html.replace(/\r/g, "\n").replace(/\t/g, " ");

// Collapse everything (e.g., "\n \n \n") into a single "\n".
html = html.replace(/(\n\s*)+/g, "\n");

// Collapse multiple horizontal spaces into one
html = html.replace(/ +/g, " ");


// 6: Overwrite the original 'data' field
const limit = $json.trim_length;
$json.data = html.trim().substring(0, limit);

// 7: Remove the configuration field from the output for cleanness
delete $json.trim_length;

// Return the cleaned item
return $json;

// --- 1: RETRIEVE DATA ---
// Your data is nested inside an "output" array within the first item.
const inputNode = $input.first();
const items = inputNode ? inputNode.json.output : [];

// Safety check
if (!items || !Array.isArray(items) || items.length === 0) {
  return [{ json: { htmlEmailBody: '<p>No items were found to create a report.</p>' } }];
}

// --- 2: BUILD THE HTML TABLE ---

// Style definitions
const tableAttributes = `width="100%" border="1" cellspacing="0" cellpadding="12"`;
const tableStyle = `font-family: Arial, sans-serif; font-size: 14px; border: 1px solid #cccccc; border-collapse: collapse;`;
const thStyle = `background-color: #f2f2f2; font-weight: bold; text-align: left; vertical-align: top; padding: 12px; border: 1px solid #cccccc;`;
const tdStyle = `text-align: left; vertical-align: top; padding: 12px; border: 1px solid #cccccc;`;
const linkStyle = `color: #1a0dab; text-decoration: none; word-break: break-all;`;
const evenRowStyle = `background-color: #f9f9f9;`;

// Helper function to clean strings 
const cleanString = (str) => {
  if (typeof str !== 'string') return str;
  // 1. Remove leading and trailing quotes
  // 2. Fix escaped quotes (e.g. \" becomes ")
  return str.replace(/^"|"$/g, '').replace(/\\"/g, '"');
};

// Helper to format the link
const formatLinkCell = (linkString) => {
  if (!linkString) return 'N/A';

  let cleanLink = cleanString(linkString);

  // Basic validation to ensure it looks like a URL
  if (!cleanLink.startsWith('http')) return cleanLink;

  return `<a href="${cleanLink}" target="_blank" style="${linkStyle}">${cleanLink}</a>`;
};

// Start the HTML string
let html = `
<table ${tableAttributes} style="${tableStyle}">
  <thead>
    <tr>
      <th style="${thStyle}">Summary</th>
      <th style="${thStyle}">Topic</th>
      <th style="${thStyle}">Link</th>
    </tr>
  </thead>
  <tbody>
`;


// --- 3: SORT ITEMS BY TOPIC (A-Z) ---
items.sort((a, b) => {
  // Get topics or default to empty string
  const topicA = cleanString(a.topic || '').toLowerCase();
  const topicB = cleanString(b.topic || '').toLowerCase();

  // Compare them alphabetically
  return topicA.localeCompare(topicB);
});
// YOUR_AWS_SECRET_KEY_HERE==

// --- 4: LOOP THROUGH ITEMS ---
for (const [index, item] of items.entries()) {
  const isEvenRow = index % 2 === 0;
  const trStyle = isEvenRow ? evenRowStyle : '';

  // 'item' is the direct object 
  // (e.g. { summary: "...", topic: "..." })

  const summary = cleanString(item.summary ?? 'N/A');
  const topic = cleanString(item.topic ?? 'N/A');
  const rawLink = item.link ?? '';

  const linkCellHtml = formatLinkCell(rawLink);

  html += `
    <tr style="${trStyle}">
      <td style="${tdStyle}">${summary}</td>
      <td style="${tdStyle}">${topic}</td>
      <td style="${tdStyle}">${linkCellHtml}</td>
    </tr>
  `;
}

// Finish the HTML
html += `
  </tbody>
</table>
`;

// --- 5: RETURN THE FINAL HTML OBJECT ---
return [{
  json: {
    htmlEmailBody: html
  }
}];

// --- 1: GET AGGREGATED DATA ---
// The Aggregate node put all your emails into an array called "data"
const inputNode = $input.first();
const emailList = inputNode.json.data;

if (!emailList || !Array.isArray(emailList) || emailList.length === 0) {
  return [];
}

// --- CONSTANTS ---
const googlePrefix = 'https://www.google.com/url?rct=j&amp;sa=t&amp;url=';
const googleSuffixMarker = '&amp;ct=';
// Regex: Matches Topic Headers OR Links
const pattern = /(<span[^>]*style="[^"]*font-size:22px[^"]*"[^>]*>(.*?)<\/span>)|(href="([^"]*)")/gi;

const rawResults = [];

// --- 2: LOOP THROUGH EACH EMAIL ---
for (const emailItem of emailList) {
  const html = emailItem.html;

  // Skip if this specific item has no HTML
  if (!html) continue;

  // Reset state for this specific email
  let currentTopic = "General"; 
  let match;

  // Reset Regex index for the new string
  pattern.lastIndex = 0;

  // --- 3: EXTRACT LINKS FROM CURRENT EMAIL ---
  while ((match = pattern.exec(html)) !== null) {

    // CASE A: Found a Topic Header (Group 2)
    if (match[2]) {
      currentTopic = match[2]
        .trim()
        .replace(/&quot;/g, '"')
        .replace(/&amp;/g, '&');
    } 

    // CASE B: Found a Link (Group 4)
    else if (match[4]) {
      const rawLink = match[4];

      if (rawLink.startsWith(googlePrefix)) {
        // Clean the link
        let cleanLink = rawLink.replace(googlePrefix, '');
        const suffixIndex = cleanLink.indexOf(googleSuffixMarker);

        if (suffixIndex !== -1) {
          cleanLink = cleanLink.substring(0, suffixIndex);
        }

        const decodedLink = decodeURIComponent(cleanLink.replace(/%25/g, '%'));

        // Add to our master list
        rawResults.push({
          topic: currentTopic,
          link: decodedLink
        });
      }
    }
  }
}

// --- STEP 4: DEDUPLICATE GLOBAL LIST ---
const uniqueLinks = [...new Map(rawResults.map(item => [item.link, item])).values()];

return uniqueLinks;
