// Clean Wikipedia Text (Regex version - No Cheerio)
const item = items[0].json;
let html = '';

// 1. Extract HTML from Input
if (typeof item === 'string') {
  html = item;
} else if (item.response?.body) {
  html = item.response.body;
} else if (item.body) {
  html = item.body;
} else if (item.content) {
  html = item.content;
} else if (item.data) {
  html = item.data;
} else {
    // Handle "exploded" string (numeric keys) from ScrapeOps
    const keys = Object.keys(item).filter(k => !isNaN(k));
    if (keys.length > 0) {
        keys.sort((a, b) => parseInt(a) - parseInt(b));
        html = keys.map(k => item[k]).join('');
    }
}

// 2. Basic Cleanup (Remove scripts, styles, tables, references)
html = html.replace(/<script\b[^>]*>[\s\S]*?<\/script>/gim, "");
html = html.replace(/<style\b[^>]*>[\s\S]*?<\/style>/gim, "");
html = html.replace(/<table\b[^>]*>[\s\S]*?<\/table>/gim, ""); 
html = html.replace(/<sup\b[^>]*class=["'][^"']*reference[^"']*["'][^>]*>[\s\S]*?<\/sup>/gim, "");
html = html.replace(/<span\b[^>]*class=["'][^"']*mw-editsection[^"']*["'][^>]*>[\s\S]*?<\/span>/gim, "");

// 3. Find the History/Origins/Background Section
const headerRegex = /<h[23][^>]*>.*?(?:History|Origins|Background).*?<\/h[23]>/i;
const headerMatch = headerRegex.exec(html);

let historyText = '';

if (headerMatch) {
    const startIndex = headerMatch.index + headerMatch[0].length;
    const remainder = html.substring(startIndex);
    const nextHeaderRegex = /<h[23][^>]*>/i;
    const nextHeaderMatch = nextHeaderRegex.exec(remainder);

    let sectionContent = remainder;
    if (nextHeaderMatch) {
        sectionContent = remainder.substring(0, nextHeaderMatch.index);
    }

    const pRegex = /<p\b[^>]*>([\s\S]*?)<\/p>/gi;
    let pMatch;
    while ((pMatch = pRegex.exec(sectionContent)) !== null) {
        let pText = pMatch[1];
        pText = pText.replace(/<[^>]+>/g, '');
        pText = pText.replace(/&nbsp;/g, ' ').replace(/&amp;/g, '&').replace(/&quot;/g, '"').replace(/&lt;/g, '<').replace(/&gt;/g, '>');
        historyText += pText.trim() + '\n\n';
    }
} else {
    // Fallback: Grab first 5 paragraphs
    const pRegex = /<p\b[^>]*>([\s\S]*?)<\/p>/gi;
    let pMatch;
    let count = 0;
    while ((pMatch = pRegex.exec(html)) !== null && count < 5) {
        let pText = pMatch[1];
        pText = pText.replace(/<[^>]+>/g, '');
        pText = pText.replace(/&nbsp;/g, ' ').replace(/&amp;/g, '&').replace(/&quot;/g, '"').replace(/&lt;/g, '<').replace(/&gt;/g, '>');
        if (pText.trim().length > 50) {
            historyText += pText.trim() + '\n\n';
            count++;
        }
    }
}

historyText = historyText.replace(/\[.*?\]/g, '').trim();

// 4. Return Data (Restore metadata from Build Wikipedia URL node)
// We use $('Construct Page URL').first().json to get the original topic/url info
const originalData = $('Construct Page URL').first().json;

return [
  {
    json: {
      ...originalData,
      history_raw: historyText
    }
  }
];

// Universal robust parser ‚Äî handles many OpenAI node shapes including message.content
const item = items[0];
let raw = '';
// try many known places the assistant text may appear
if (item.json.output_text) {
  raw = item.json.output_text;
} else if (item.json.content?.[0]?.text) {
  raw = item.json.content[0].text;
} else if (item.json.output?.[0]?.content?.[0]?.text) {
  raw = item.json.output[0].content[0].text;
} else if (item.json.choices?.[0]?.message?.content) {
  raw = item.json.choices[0].message.content;
} else if (item.json.choices?.[0]?.text) {
  raw = item.json.choices[0].text;
} else if (typeof item.json.message?.content === 'string') {
  // your specific shape: item.json.message.content is a string with JSON
  raw = item.json.message.content;
} else if (Array.isArray(item.json.output) && item.json.output[0]?.content?.[0]?.text) {
  raw = item.json.output[0].content[0].text;
} else {
  raw = JSON.stringify(item.json);
}
// normalize
raw = String(raw).trim();
// remove triple backticks if present
raw = raw.replace(/^\s*```(?:json)?\s*/i, '');
raw = raw.replace(/\s*```\s*$/i, '');
// extract the first {...} JSON block
const match = raw.match(/\{[\s\S]*\}/);
const jsonText = match ? match[0] : raw;
// parse with fallback (handle escaped newlines)
let parsed;
try {
  parsed = JSON.parse(jsonText);
} catch (e1) {
  try {
    parsed = JSON.parse(jsonText.replace(/\\n/g, '\n'));
  } catch (e2) {
    return [{ json: { parse_error: e2.message, raw } }];
  }
}
// tolerant key lookup
function getVal(obj, names){
  for (const n of names){
    if (Object.prototype.hasOwnProperty.call(obj, n)) return obj[n];
  }
  // case-insensitive normalized
  const norm = {};
  for (const k of Object.keys(obj||{})) norm[k.toLowerCase().replace(/[_\s]+/g,'')] = obj[k];
  for (const n of names){
    const key = n.toLowerCase().replace(/[_\s]+/g,'');
    if (norm[key] !== undefined) return norm[key];
  }
  return undefined;
}
// build output
const out = {};
out.Topic = getVal(parsed, ['Topic','topic']) || '';
out.Search_Query_URL = getVal(parsed, ['Search_Query_URL','search_query_url','searchqueryurl']) || '';
out.Wikipedia_Page_Title = getVal(parsed, ['Wikipedia_Page_Title','Wikipedia Page Title','wikipedia_page_title']) || '';
out.Wikipedia_PAGE_URL = getVal(parsed, ['Wikipedia_PAGE_URL','Wikipedia Page URL','wikipedia_page_url']) || '';
out.History_Raw = getVal(parsed, ['History_Raw','history_raw']) || '';
out.History_Cleaned = getVal(parsed, ['History_Cleaned','history_cleaned']) || '';
out.History_Summary = getVal(parsed, ['History_Summary','history_summary']) || '';
// handle Timeline: array -> newline string, or string (maybe JSON string)
let timeline = getVal(parsed, ['Timeline','timeline','TimeLine']);
if (Array.isArray(timeline)) {
  out.Timeline = timeline.join('\n');
} else if (typeof timeline === 'string') {
  try {
    const maybe = JSON.parse(timeline);
    out.Timeline = Array.isArray(maybe) ? maybe.join('\n') : timeline;
  } catch (e) {
    out.Timeline = timeline;
  }
} else {
  out.Timeline = '';
}
// unescape double-escaped newlines
for (const k of ['History_Raw','History_Cleaned','History_Summary','Timeline']) {
  if (typeof out[k] === 'string') out[k] = out[k].replace(/\\n/g, '\n');
}
return [{ json: out }];

// Get topic from Set Topic node (not from Wikipedia API response)
const topic = $('Set Topic').first().json.topic;
const search = items[0].json.query.search;

if (!search || search.length === 0) {
  throw new Error("No Wikipedia page found for: " + topic);
}

const title = search[0].title;
const encoded = encodeURIComponent(title.replace(/ /g, "_"));

return [
  {
    json: {
      topic,
      wikipedia_page_title: title,
      wikipedia_page_url: `https://en.wikipedia.org/wiki/${encoded}`,
      search_query_url: `https://en.wikipedia.org/w/api.php?action=query&list=search&srsearch=${encodeURIComponent(topic)}&format=json`
    }
  }
];
