// Compute from/to in local ISO8601 with timezone
const sd = this.getWorkflowStaticData('global');
const now = new Date();
const pollMinutes = 5; // adjust if you change cron
let fromDate;
if (sd.lastRun) fromDate = new Date(sd.lastRun); else fromDate = new Date(now.getTime() - (pollMinutes + 1) * 60000);
// helper to produce local ISO without ms +TZ
function toLocalISO(date) {
  const off = -date.getTimezoneOffset();
  const sign = off >= 0 ? '+' : '-';
  const pad = n => String(Math.floor(Math.abs(n))).padStart(2, '0');
  const hrs = pad(off / 60);
  const mins = pad(off % 60);
  return date.toISOString().split('.')[0] + sign + hrs + ':' + mins;
}
const from = toLocalISO(fromDate);
const to = toLocalISO(now);
// don't update lastRun here ‚Äî update after processing to avoid gaps
return [{ json: { from, to } }];

// Build one item per module from modules list response
const res = items[0] && items[0].json ? items[0].json : {};
const modulesRaw = Array.isArray(res.modules) ? res.modules : (res.modules || []);
// Filter out system modules optionally (blacklist) - you can adjust this array if needed
const requiredModule = ['Leads','Contacts','Accounts','Deals'];
const modules = modulesRaw.map(m => m.api_name || m.module_name).filter(m => m && requiredModule.includes(m));
const from = $json.from || new Date().toISOString();
return modules.map(m => ({ json: { module: m, from_local: from } }));

// Expand responses into records and set module by index
const out = [];
const requiredModule = ['Leads','Contacts','Accounts','Deals'];

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const resp = item.json || {};
  const body = resp.body || resp;

  // module name: prefer requiredModule by index, else any module field in response, else Unknown
  const moduleName = requiredModule[i] || body.module || resp.module || item?.pairedItem?.json?.module || 'Unknown';

  // Get records array from body.data (Zoho response) or resp.data
  const records = Array.isArray(body.data) ? body.data : (Array.isArray(resp.data) ? resp.data : []);

  if (!records || records.length === 0) continue;

  for (const rec of records) {
    const r = { ...rec };
    r.module = moduleName;
    r.recordId = r.id || r.ID || r.record_id || r.recordId || '';
    r.timestamp = r.Modified_Time || r.Created_Time || new Date().toISOString();
    out.push({ json: r });
  }
}

return out;

// Detect unusual patterns (module-agnostic) - unchanged but robustified
for (const item of items) {
  const entry = item.json;
  const changed = (entry.changed_fields || entry.Changed_Fields || entry.changedFields || '').toString().toLowerCase();
  const event = (entry.event || entry.Action || '').toString().toLowerCase();
  let suspicious = false;
  const reasons = [];
  if (event.includes('delete')) { suspicious = true; reasons.push('deletion'); }
  if (changed.includes('owner') || changed.includes('assigned_to') || changed.includes('owner_id')) { suspicious = true; reasons.push('owner_changed'); }
  if (changed.includes('email') || changed.includes('contact_email')) { suspicious = true; reasons.push('email_changed'); }
  if (changed.includes('role') || changed.includes('is_admin') || changed.includes('profile')) { suspicious = true; reasons.push('role_or_privilege_change'); }
  const fieldsCount = changed ? changed.split(',').filter(f => f.trim()).length : 0;
  if (fieldsCount >= 10) { suspicious = true; reasons.push('many_fields_changed'); }
  entry.suspicious = suspicious;
  entry.suspicious_reasons = reasons.join(',');
  entry.fields_changed_count = fieldsCount;
  entry.module = entry.module || entry.Module || 'Unknown';
  entry.recordId = entry.recordId || entry.id || entry.ID || entry.record_id || '';
  entry.timestamp = entry.timestamp || entry.Modified_Time || entry.Created_Time || new Date().toISOString();
}
return items;

// Create audit JSON file as binary attachment for each item
for (const item of items) {
  const data = JSON.stringify(item.json, null, 2);
  const b64 = Buffer.from(data).toString('base64');
  item.binary = { audit: { data: b64, fileName: 'audit-' + (item.json.timestamp || new Date().toISOString()).replace(/[:.]/g, '-') + '.json', mimeType: 'application/json' } };
}
return items;

// Update lastRun after processing (use UTC ISO for storage)
const sd = this.getWorkflowStaticData('global');
sd.lastRun = new Date().toISOString();
return items;
