// 1. Get Static Data
const staticData = $('Define Initial Prompt & Test Data').first().json;
const inputData = items[0].json;

// YOUR_AWS_SECRET_KEY_HERE==
// 2. Update Loop Count (Adopt AI calculation)
// YOUR_AWS_SECRET_KEY_HERE==
let currentLoopCount = 1;

if (inputData.next_loop_count) {
    // If AI says "Next is loop 3", adopt it
    currentLoopCount = inputData.next_loop_count;
} else if (inputData.loop_count) {
    // Maintain previous value if exists
    currentLoopCount = inputData.loop_count;
}

// YOUR_AWS_SECRET_KEY_HERE==
// 3. Update Prompt
// YOUR_AWS_SECRET_KEY_HERE==
let nextPrompt = staticData.initial_prompt;
let currentScore = 0;

// 2nd loop onwards
if (currentLoopCount > 1) {
    // First, get and keep previous prompt (prevent reset)
    try {
        const prev = $('Manage Loop & State').item.json.current_prompt;
        if (prev) nextPrompt = prev;
    } catch(e) {}

    // Get Score
    try {
        // Get score from Evaluator output
        currentScore = $('AI Response Evaluator').item.json.output.score || 0;
    } catch(e) {}

    // Update if there is an improvement proposal from AI
    if (inputData.improved_prompt) {
        nextPrompt = inputData.improved_prompt;
    }
}

// YOUR_AWS_SECRET_KEY_HERE==
// 4. Output
// YOUR_AWS_SECRET_KEY_HERE==
return [{
  json: {
    // Static Data
    test_input: staticData.test_input,
    ground_truth: staticData.ground_truth,
    initial_prompt: staticData.initial_prompt,

    // Updated Data
    current_prompt: nextPrompt,
    loop_count: currentLoopCount,
    current_score: currentScore,

    // Stop Condition (Over 5 loops OR Error prevention limit OR Score >= 95)
    stop_loop: currentLoopCount > 5 || currentScore >= 95
  }
}];

