// Erwartet: items[] aus dem Facebook Graph API Node
// Wir bauen eine flache Struktur pro Kampagne/Adset/Ad

const out = [];

for (const item of items) {
  // Meta liefert die Ergebnisse in item.json.data als Array
  const dataArray = Array.isArray(item.json.data) ? item.json.data : [];

  for (const s of dataArray) {
    const row = {};

    // Basisinfo & Struktur
    row.date = s.date_start || s.date_stop || null;
    row.account_id = s.account_id || null;
    row.account_name = s.account_name || null;

    row.campaign_id = s.campaign_id || null;
    row.campaign_name = s.campaign_name || null;
    row.objective = s.objective || null;

    row.adset_id = s.adset_id || null;
    row.adset_name = s.adset_name || null;
    row.ad_id = s.ad_id || null;
    row.ad_name = s.ad_name || null;

    row.publisher_platform = s.publisher_platform || null;

    // Standardmetriken
    row.impressions = Number(s.impressions || 0);
    row.reach = Number(s.reach || 0);
    row.frequency = Number(s.frequency || 0);
    row.spend = Number(s.spend || 0);
    row.clicks = Number(s.clicks || 0);
    row.inline_link_clicks = Number(s.inline_link_clicks || 0);
    row.ctr = Number(s.ctr || 0);
    row.cpc = Number(s.cpc || 0);
    row.cpm = Number(s.cpm || 0);

    // Actions & Values
    const actions = Array.isArray(s.actions) ? s.actions : [];
    const actionValues = Array.isArray(s.action_values) ? s.action_values : [];

    const getAction = (type) => {
      const f = actions.find(a => a.action_type === type);
      return f ? Number(f.value || 0) : 0;
    };

    const getActionValue = (type) => {
      const f = actionValues.find(a => a.action_type === type);
      return f ? Number(f.value || 0) : 0;
    };

    // Konversionsmetriken fÃ¼r diverse Ziele
    row.leads = getAction('lead');
    row.on_facebook_lead = getAction('onsite_conversion.lead_grouped');

    row.purchases = getAction('purchase');
    row.purchase_value = getActionValue('purchase');

    row.add_to_cart = getAction('add_to_cart');
    row.initiate_checkout = getAction('initiate_checkout');

    // Abgeleitete KPIs
    row.cpl = row.leads > 0 ? row.spend / row.leads : null;
    row.cpa = row.purchases > 0 ? row.spend / row.purchases : null;
    row.roas = row.purchase_value > 0 && row.spend > 0
      ? row.purchase_value / row.spend
      : null;

    out.push({ json: row });
  }
}

return out;

