const body = $input.first().json.body;

return [{
  json: {
    owner: body.repository.owner.login,
    repo: body.repository.name,
    currentTag: body.release.tag_name,
    currentReleaseId: body.release.id,
    currentReleaseUrl: body.release.html_url,
  }
}];

// Take the compare response from the previous node
const compare = $input.first().json;

const commits = compare.commits || [];
const files = compare.files || [];

// Determine if there are any changes at all
const hasChanges =
  (compare.total_commits && compare.total_commits > 0) ||
  (files.length > 0);

// Simplify commits: only keep what the AI really needs
const simplifiedCommits = commits.map(c => ({
  sha: c.sha,
  message: c.commit?.message,
  author: c.commit?.author?.name,
  date: c.commit?.author?.date,
}));

// Simplify files: filename + status + quick stats + patch
const simplifiedFiles = files.map(f => ({
  filename: f.filename,
  status: f.status,  // added | modified | removed
  additions: f.additions,
  deletions: f.deletions,
  changes: f.changes,
  patch: f.patch,
}));

return [{
  json: {
    hasChanges,
    status: compare.status,
    ahead_by: compare.ahead_by,
    behind_by: compare.behind_by,
    total_commits: compare.total_commits,
    commits: simplifiedCommits,
    files: simplifiedFiles,
    // Optional: if you already merged tags into this item upstream:
    previousTag: $('Get the last two release tags').first().json.previousTag,
    currentTag: $('Get the last two release tags').first().json.currentTag,
  }
}];
