// Normalize Indeed Jobs data
const jobs = $input.all();
const companyData = $('Google Sheets - Get Target Companies').first().json;
const runDate = $('Set Configuration').first().json.runDate;

return jobs.map(job => ({
  json: {
    source: 'Indeed',
    title: job.json.title || job.json.positionName || '',
    company: job.json.company || job.json.companyName || '',
    location: job.json.location || '',
    description: job.json.description || '',
    postedAt: job.json.postedAt || job.json.postingDate || '',
    applyLink: job.json.url || job.json.link || '',
    salary: job.json.salary || '',
    targetCompany: companyData['Company Name'] || '',
    targetKeywords: companyData['Target Keywords'] || '',
    mySolution: companyData['My Solution'] || '',
    scrapedDate: runDate,
    jobId: `indeed-${job.json.title}-${job.json.company}`.replace(/\s+/g, '-').toLowerCase().substring(0, 100)
  }
}));

// Deduplicate jobs by jobId and filter recent jobs
const jobs = $input.all();
const daysThreshold = $('Set Configuration').first().json.daysToCheck || 7;
const now = new Date();
const thresholdDate = new Date(now.getTime() - (daysThreshold * 24 * 60 * 60 * 1000));

// Helper function to parse relative dates
function parseDaysAgo(dateString) {
  if (!dateString) return new Date();

  const daysAgoMatch = dateString.match(/(\d+)\s*days?\s*ago/i);
  if (daysAgoMatch) {
    return new Date(now.getTime() - (parseInt(daysAgoMatch[1]) * 24 * 60 * 60 * 1000));
  }

  const hoursAgoMatch = dateString.match(/(\d+)\s*hours?\s*ago/i);
  if (hoursAgoMatch) {
    return new Date(now.getTime() - (parseInt(hoursAgoMatch[1]) * 60 * 60 * 1000));
  }

  if (dateString.toLowerCase().includes('today') || dateString.toLowerCase().includes('just posted')) {
    return new Date();
  }
  if (dateString.toLowerCase().includes('yesterday')) {
    return new Date(now.getTime() - (24 * 60 * 60 * 1000));
  }

  const parsedDate = new Date(dateString);
  return !isNaN(parsedDate.getTime()) ? parsedDate : new Date();
}

// Deduplicate by jobId
const seen = new Set();
const uniqueJobs = [];

for (const job of jobs) {
  const jobId = job.json.jobId;
  if (!seen.has(jobId)) {
    seen.add(jobId);

    // Check if job is within threshold
    const jobDate = parseDaysAgo(job.json.postedAt);
    if (jobDate >= thresholdDate) {
      uniqueJobs.push(job);
    }
  }
}

return uniqueJobs;

// Filter jobs by target keywords and add matched keyword info
const jobs = $input.all();
const filteredJobs = [];

for (const job of jobs) {
  const targetKeywords = (job.json.targetKeywords || '').split(',').map(k => k.trim().toLowerCase()).filter(k => k);
  const combinedText = (job.json.title + ' ' + job.json.description).toLowerCase();

  let matchedKeyword = null;
  for (const keyword of targetKeywords) {
    if (combinedText.includes(keyword)) {
      matchedKeyword = keyword;
      break;
    }
  }

  if (matchedKeyword) {
    filteredJobs.push({
      json: {
        ...job.json,
        matchedKeyword: matchedKeyword
      }
    });
  }
}

return filteredJobs.length > 0 ? filteredJobs : [{ json: { noMatches: true } }];

// Aggregate weekly statistics
const rawJobs = $('Google Sheets - Get Raw Jobs (Weekly)').all();
const qualifiedLeads = $('Google Sheets - Get Qualified Leads (Weekly)').all();
const weekStart = $('Set - Weekly Report Config').first().json.reportWeekStart;
const weekEnd = $('Set - Weekly Report Config').first().json.reportWeekEnd;

// Filter to this week's data
const thisWeekJobs = rawJobs.filter(job => {
  const jobDate = job.json.scrapedDate;
  return jobDate >= weekStart && jobDate <= weekEnd;
});

const thisWeekLeads = qualifiedLeads.filter(lead => {
  const leadDate = lead.json.scrapedDate || lead.json.date;
  return leadDate >= weekStart && leadDate <= weekEnd;
});

// Calculate statistics
const stats = {
  totalJobs: thisWeekJobs.length,
  totalQualifiedLeads: thisWeekLeads.length,
  conversionRate: thisWeekJobs.length > 0 ? ((thisWeekLeads.length / thisWeekJobs.length) * 100).toFixed(1) : 0,
  bySource: {},
  byKeyword: {},
  topCompanies: {},
  highUrgency: 0,
  mediumUrgency: 0,
  lowUrgency: 0,
  avgUrgency: 0
};

// Count by source
for (const job of thisWeekJobs) {
  const source = job.json.source || 'Unknown';
  stats.bySource[source] = (stats.bySource[source] || 0) + 1;
}

// Count by matched keyword and companies from leads
let totalUrgency = 0;
for (const lead of thisWeekLeads) {
  const keyword = lead.json.matchedKeyword || 'Unknown';
  stats.byKeyword[keyword] = (stats.byKeyword[keyword] || 0) + 1;

  const company = lead.json.company || 'Unknown';
  stats.topCompanies[company] = (stats.topCompanies[company] || 0) + 1;

  const urgency = parseInt(lead.json.urgencyScore) || 5;
  totalUrgency += urgency;

  if (urgency >= 8) stats.highUrgency++;
  else if (urgency >= 5) stats.mediumUrgency++;
  else stats.lowUrgency++;
}

stats.avgUrgency = thisWeekLeads.length > 0 ? (totalUrgency / thisWeekLeads.length).toFixed(1) : 0;

const sortedKeywords = Object.entries(stats.byKeyword).sort((a, b) => b[1] - a[1]).slice(0, 5);
const sortedCompanies = Object.entries(stats.topCompanies).sort((a, b) => b[1] - a[1]).slice(0, 5);

return [{
  json: {
    weekStart,
    weekEnd,
    stats,
    topKeywords: sortedKeywords,
    topCompanies: sortedCompanies,
    rawJobsData: thisWeekJobs.map(j => j.json),
    leadsData: thisWeekLeads.map(l => l.json)
  }
}];

