const raw = $json.data;

const lines = raw.split(/\r?\n/);

const segments = [];
let current = null;

function cleanText(t) {
  return t
    .replace(/<c>/g, "")
    .replace(/<\/c>/g, "")
    .replace(/<\d+:\d+:\d+\.\d+>/g, "")
    .replace(/\s+/g, " ")
    .trim();
}

const timestampRegex = /(\d{2}:\d{2}:\d{2}\.\d{3})\s*-->\s*(\d{2}:\d{2}:\d{2}\.\d{3})/;

for (let line of lines) {
  line = line.trim();
  if (!line) continue;

  // If line contains a timestamp line
  const match = line.match(timestampRegex);
  if (match) {
    // Save previous
    if (current && current.text.trim()) {
      segments.push({
        start: current.start,
        end: current.end,
        text: cleanText(current.text)
      });
    }

    current = {
      start: match[1],
      end: match[2],
      text: ""
    };

    continue;
  }

  // Text line
  if (current) {
    current.text += " " + cleanText(line);
  }
}

// Push last segment
if (current && current.text.trim()) {
  segments.push({
    start: current.start,
    end: current.end,
    text: cleanText(current.text)
  });
}

return segments;

const items = $items();
const size = 150; // number of caption segments per chunk

let chunks = [];
for (let i = 0; i < items.length; i += size) {
    const slice = items.slice(i, i + size);
    chunks.push({
        json: {
            chunkIndex: chunks.length,
            captions: slice.map(s => s.json)
        }
    });
}

return chunks;

