const results = [];
const items = $items();

// Loop all AI responses
for (const item of items) {
  try {
    // The JSON output returned as text string
    const raw = item.json.content.parts[0].text;

    // Parse the JSON array inside the string
    const clips = JSON.parse(raw);

    // Add clips to results
    results.push(...clips);
  } catch (e) {
    console.log("Error parsing:", e);
  }
}

// Sort by score (highest first)
results.sort((a, b) => b.score - a.score);

// OPTIONAL: keep only top 10 clips
const topClips = results.slice(0, 10);

return topClips.map(c => ({ json: c }));

// Get all incoming items from Join node
const items = $input.all();

// First item = video metadata
const videoItem = items[0].json;
const videoPath = videoItem.downloadedFile;

// Remaining items = clip candidates from AI
const clips = items.slice(1).map(i => i.json);

// Build merged output
const merged = clips.map(c => ({
  json: {
    start: c.start,
    end: c.end,
    hook: c.hook,
    score: c.score,
    videoPath: videoPath
  }
}));

return merged;

// Get all items from FFmpeg Execute Command node
const items = $input.all();

const output = [];

for (const item of items) {
  const stderr = item.json.stderr || "";

  // Extract path: Output #0, mp4, to '...'
  const match = stderr.match(/to '([^']+)'/);

  const outputFile = match ? match[1] : null;

  output.push({
    json: {
      outputFile,
      exitCode: item.json.exitCode,
      start: item.json.start,
      end: item.json.end,
      hook: item.json.hook,
      score: item.json.score
    }
  });
}

return output;

