// Process all input items, not just the first one
const items = $input.all();
const results = [];

items.forEach((item, index) => {
  const raw = item.json.data || "";

  // Define patterns to remove or clean
  const cleaningPatterns = [
    // Remove image markdown but preserve alt text for context
    { pattern: /!\[([^\]]*)\]\([^)]*\)/g, replacement: '$1' },

    // Remove URLs and links but preserve link text
    { pattern: /\[([^\]]+)\]\([^)]*\)/g, replacement: '$1' },

    // Remove standalone URLs
    { pattern: /https?:\/\/[^\s]+/g, replacement: '' },

    // Clean up markdown formatting characters
    { pattern: /[#*_`>]+/g, replacement: '' },

    // Remove multiple dashes or underscores
    { pattern: /[-_]{2,}/g, replacement: '' },

    // Remove email addresses
    { pattern: /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/g, replacement: '' },

    // Clean up escaped characters
    { pattern: /\\(.)/g, replacement: '$1' },

    // Remove excessive punctuation
    { pattern: /[.,!?]{2,}/g, replacement: '.' }
  ];

  // Sections to completely remove (case-insensitive)
  const sectionsToRemove = [
    /toggle\s+menu/gi,
    /previous\s+slide\s*next/gi,
    /log\s+in/gi,
    /sign\s+up/gi,
    /book\s+a\s+demo/gi,
    /contact\s+sales/gi,
    /©.*202\d/gi, // Copyright notices
    /follow\s+us/gi,
    /social\s+media/gi
  ];

  // Navigation and UI elements patterns
  const uiPatterns = [
    /^\s*\d+\s*$/gm, // Standalone numbers (likely UI elements)
    /^\s*(docs|tickers|sources|think|send)\s*$/gmi,
    /^\s*toggle\s*$/gmi
  ];

  let cleaned = raw;

  // Apply cleaning patterns
  cleaningPatterns.forEach(({pattern, replacement}) => {
    cleaned = cleaned.replace(pattern, replacement);
  });

  // Remove unwanted sections
  sectionsToRemove.forEach(pattern => {
    cleaned = cleaned.replace(pattern, '');
  });

  // Remove UI patterns
  uiPatterns.forEach(pattern => {
    cleaned = cleaned.replace(pattern, '');
  });

  // Normalize whitespace and structure
  cleaned = cleaned
    // Replace multiple spaces with single space
    .replace(/[ \t]+/g, ' ')
    // Replace multiple newlines with double newline (paragraph breaks)
    .replace(/\n{3,}/g, '\n\n')
    // Remove lines with only whitespace
    .replace(/^\s*$/gm, '')
    // Remove leading/trailing whitespace from each line
    .split('\n').map(line => line.trim()).join('\n')
    // Remove empty lines at start and end
    .replace(/^\n+|\n+$/g, '')
    // Fix spacing around punctuation
    .replace(/\s+([.,!?])/g, '$1')
    // Ensure space after punctuation
    .replace(/([.,!?])([A-Za-z])/g, '$1 $2');

  // Additional content-specific cleaning
  cleaned = cleaned
    // Remove redundant phrases
    .replace(/see\s+the\s+benchmark→?/gi, '')
    .replace(/see\s+all\s+benchmarks?→?/gi, '')
    // Clean up percentage comparisons
    .replace(/(\w+)(\d+%)/g, '$1 $2')
    // Fix common spacing issues
    .replace(/([a-z])([A-Z])/g, '$1 $2') // Add space between camelCase
    .replace(/(\w)(\d)/g, '$1 $2') // Space between word and number
    .replace(/(\d)(\w)/g, '$1 $2'); // Space between number and word

  // Final cleanup - remove very short lines that are likely UI artifacts
  const lines = cleaned.split('\n').filter(line => {
    const trimmed = line.trim();
    // Keep lines that are either longer than 3 chars or contain meaningful content
    return trimmed.length > 3 || /^[A-Z][A-Za-z\s]+$/.test(trimmed);
  });

  cleaned = lines.join('\n').trim();

  // Add the processed item to results with proper n8n format
  results.push({
    json: {
      ...item.json, // Preserve all original data
      cleaned_markdown: cleaned,
      original_length: raw.length,
      cleaned_length: cleaned.length,
      reduction_percent: Math.round((1 - cleaned.length / raw.length) * 100)
    },
    pairedItem: { item: index } // Maintain pairing
  });
});

// Return all processed items
return results;

