// Robust parser for weirdly-wrapped payloads in n8n
function toObject(input) {
  if (input == null) return null;

  // If it's a string, try to JSON.parse it, and recurse
  if (typeof input === 'string') {
    try {
      const parsed = JSON.parse(input);
      return toObject(parsed);
    } catch {
      return null; // string but not JSON
    }
  }

  // If it's an array, take the first element and recurse
  if (Array.isArray(input)) {
    if (input.length === 0) return null;
    return toObject(input[0]);
  }

  // If it's already an object, we're done
  if (typeof input === 'object') {
    return input;
  }

  return null;
}

// Try common locations
const raw =
  $json.body ??
  $json.payload ??
  $json.data ??
  $json.requestBody ??
  $json; // last resort

const obj = toObject(raw);

if (!obj || typeof obj !== 'object') {
  throw new Error(
    'Could not parse payload into an object. Saw: ' +
    JSON.stringify(raw)?.slice(0, 250)
  );
}

return [{ json: obj }];
