  // Your HTTP node sometimes returns an array at top-level
  const payload = item.json;
  const first = Array.isArray(payload) ? payload[0] : payload;

  // Get the stringified resultJson safely
  const resultJsonStr = first?.data?.resultJson ?? '';

  // Parse and extract
  let resultUrls = [];
  try {
    const parsed = JSON.parse(resultJsonStr);
    resultUrls = parsed?.resultUrls ?? [];
  } catch (e) {
    // leave resultUrls as []
  }

  out.push({ json: { resultUrls } });
}

return out;

