const leadData = $input.first().json;
const phone = leadData.Phone || '';
const country = leadData.Country || '';
const state = leadData.State || '';

let timezone = 'America/New_York';

if (country === 'United States' || country === 'USA') {
  const stateTimezones = {
    'California': 'America/Los_Angeles',
    'New York': 'America/New_York',
    'Texas': 'America/Chicago',
    'Florida': 'America/New_York',
    'Illinois': 'America/Chicago',
    'Arizona': 'America/Phoenix'
  };
  timezone = stateTimezones[state] || 'America/New_York';
} else if (country === 'United Kingdom' || country === 'UK') {
  timezone = 'Europe/London';
} else if (country === 'Germany') {
  timezone = 'Europe/Berlin';
} else if (country === 'Australia') {
  timezone = 'Australia/Sydney';
}

return {
  json: {
    ...leadData,
    detectedTimezone: timezone
  }
};

const config = $('Workflow Configuration').first().json;
const events = $input.all().map(item => item.json);
const meetingDuration = config.meetingDuration;
const bufferBefore = config.bufferTimeBefore;
const bufferAfter = config.bufferTimeAfter;
const workStart = config.workingHoursStart;
const workEnd = config.workingHoursEnd;
const daysAhead = config.daysToLookAhead;
const timezone = $('Detect Lead Timezone').first().json.detectedTimezone;

const availableSlots = [];
const now = new Date();

for (let day = 0; day < daysAhead; day++) {
  const currentDay = new Date(now);
  currentDay.setDate(now.getDate() + day);
  const dayStart = new Date(currentDay.toDateString() + ' ' + workStart);
  const dayEnd = new Date(currentDay.toDateString() + ' ' + workEnd);

  let currentTime = dayStart;

  while (currentTime < dayEnd) {
    const slotEnd = new Date(currentTime.getTime() + meetingDuration * 60000);
    if (slotEnd > dayEnd) break;

    const slotStartWithBuffer = new Date(currentTime.getTime() - bufferBefore * 60000);
    const slotEndWithBuffer = new Date(slotEnd.getTime() + bufferAfter * 60000);

    const hasConflict = events.some(event => {
      const eventStart = new Date(event.start.dateTime || event.start.date);
      const eventEnd = new Date(event.end.dateTime || event.end.date);
      return (slotStartWithBuffer < eventEnd && slotEndWithBuffer > eventStart);
    });

    if (!hasConflict && currentTime > now) {
      availableSlots.push({
        startTime: currentTime.toISOString(),
        endTime: slotEnd.toISOString(),
        timezone: timezone
      });
    }

    currentTime = new Date(currentTime.getTime() + 30 * 60000);
  }
}

return {
  json: {
    availableSlots: availableSlots.slice(0, 5),
    hasSlots: availableSlots.length > 0
  }
};

